base.buffer.cpp:
#include "pxtbase.h"
#include <limits.h>

using namespace std;

//% indexerGet=BufferMethods::getByte indexerSet=BufferMethods::setByte
namespace BufferMethods {
//%
uint8_t *getBytes(Buffer buf) {
    return buf->data;
}

//%
int getByte(Buffer buf, int off) {
    if (buf && 0 <= off && off < buf->length)
        return buf->data[off];
    return 0;
}

//%
void setByte(Buffer buf, int off, int v) {
    if (buf && 0 <= off && off < buf->length)
        buf->data[off] = v;
}

/**
 * Reads an unsigned byte at a particular location
 */
//%
int getUint8(Buffer buf, int off) {
    return getByte(buf, off);
}

/**
 * Returns false when the buffer can be written to.
 */
//%
bool isReadOnly(Buffer buf) {
    return buf->isReadOnly();
}

/**
 * Writes an unsigned byte at a particular location
 */
//%
void setUint8(Buffer buf, int off, int v) {
    setByte(buf, off, v);
}

int writeBuffer(Buffer buf, int dstOffset, Buffer src, int srcOffset = 0, int length = -1) {
    if (length < 0)
        length = src->length;

    if (srcOffset < 0 || dstOffset < 0 || dstOffset > buf->length)
        return -1;

    length = pxt::min(src->length - srcOffset, buf->length - dstOffset);

    if (length < 0)
        return -1;

    if (buf == src) {
        memmove(buf->data + dstOffset, src->data + srcOffset, length);
    } else {
        memcpy(buf->data + dstOffset, src->data + srcOffset, length);
    }

    return 0;
}

/**
 * Write a number in specified format in the buffer.
 */
//%
void setNumber(Buffer buf, NumberFormat format, int offset, TNumber value) {
    if (offset < 0)
        return;
    setNumberCore(buf->data + offset, buf->length - offset, format, value);
}

/**
 * Read a number in specified format from the buffer.
 */
//%
TNumber getNumber(Buffer buf, NumberFormat format, int offset) {
    if (offset < 0)
        return fromInt(0);
    return getNumberCore(buf->data + offset, buf->length - offset, format);
}

/** Returns the length of a Buffer object. */
//% property
int length(Buffer s) {
    return s->length;
}

/**
 * Fill (a fragment) of the buffer with given value.
 */
//%
void fill(Buffer buf, int value, int offset = 0, int length = -1) {
    if (offset < 0 || offset > buf->length)
        return; // DEVICE_INVALID_PARAMETER;
    if (length < 0)
        length = buf->length;
    length = pxt::min(length, buf->length - offset);
    memset(buf->data + offset, value, length);
}

/**
 * Return a copy of a fragment of a buffer.
 */
//%
Buffer slice(Buffer buf, int offset = 0, int length = -1) {
    offset = pxt::min((int)buf->length, offset);
    if (length < 0)
        length = buf->length;
    length = pxt::min(length, buf->length - offset);
    return mkBuffer(buf->data + offset, length);
}

/**
 * Shift buffer left in place, with zero padding.
 * @param offset number of bytes to shift; use negative value to shift right
 * @param start start offset in buffer. Default is 0.
 * @param length number of elements in buffer. If negative, length is set as the buffer length minus
 * start. eg: -1
 */
//%
void shift(Buffer buf, int offset, int start = 0, int length = -1) {
    if (length < 0)
        length = buf->length - start;
    if (start < 0 || start + length > buf->length || start + length < start || length == 0 ||
        offset == 0 || offset == INT_MIN)
        return;
    if (offset <= -length || offset >= length) {
        fill(buf, 0);
        return;
    }

    uint8_t *data = buf->data + start;
    if (offset < 0) {
        offset = -offset;
        memmove(data + offset, data, length - offset);
        memset(data, 0, offset);
    } else {
        length = length - offset;
        memmove(data, data + offset, length);
        memset(data + length, 0, offset);
    }
}

/**
 * Convert a buffer to string assuming UTF8 encoding
 */
//%
String toString(Buffer buf) {
    return mkString((char *)buf->data, buf->length);
}

/**
 * Convert a buffer to its hexadecimal representation.
 */
//%
String toHex(Buffer buf) {
    const char *hex = "0123456789abcdef";
    auto res = mkStringCore(NULL, buf->length * 2);
    for (int i = 0; i < buf->length; ++i) {
        res->ascii.data[i << 1] = hex[buf->data[i] >> 4];
        res->ascii.data[(i << 1) + 1] = hex[buf->data[i] & 0xf];
    }
    return res;
}

/**
 * Rotate buffer left in place.
 * @param offset number of bytes to shift; use negative value to shift right
 * @param start start offset in buffer. Default is 0.
 * @param length number of elements in buffer. If negative, length is set as the buffer length minus
 * start. eg: -1
 */
//%
void rotate(Buffer buf, int offset, int start = 0, int length = -1) {
    if (length < 0)
        length = buf->length - start;
    if (start < 0 || start + length > buf->length || start + length < start || length == 0 ||
        offset == 0 || offset == INT_MIN)
        return;

    if (offset < 0)
        offset += length << 8; // try to make it positive
    offset %= length;
    if (offset < 0)
        offset += length;

    uint8_t *data = buf->data + start;

    uint8_t *n_first = data + offset;
    uint8_t *first = data;
    uint8_t *next = n_first;
    uint8_t *last = data + length;

    while (first != next) {
        uint8_t tmp = *first;
        *first++ = *next;
        *next++ = tmp;
        if (next == last) {
            next = n_first;
        } else if (first == n_first) {
            n_first = next;
        }
    }
}

/**
 * Write contents of `src` at `dstOffset` in current buffer.
 */
//%
void write(Buffer buf, int dstOffset, Buffer src) {
    // srcOff and length not supported, we only do up to 4 args :/
    writeBuffer(buf, dstOffset, src, 0, -1);
}

/**
 * Compute k-bit FNV-1 non-cryptographic hash of the buffer.
 */
//%
uint32_t hash(Buffer buf, int bits) {
    if (bits < 1)
        return 0;
    uint32_t h = hash_fnv1(buf->data, buf->length);
    if (bits >= 32)
        return h;
    else
        return ((h ^ (h >> bits)) & ((1 << bits) - 1));
}

} // namespace BufferMethods

bool BoxedBuffer::isInstance(TValue v) {
    return getAnyVTable(v) == &buffer_vt;
}

// The functions below are deprecated in control namespace, but they are referenced
// in Buffer namespaces via explicit shim=...
namespace control {
/**
 * Create a new zero-initialized buffer.
 * @param size number of bytes in the buffer
 */
//% deprecated=1
Buffer createBuffer(int size) {
    return mkBuffer(NULL, size);
}

/**
 * Create a new buffer with UTF8-encoded string
 * @param str the string to put in the buffer
 */
//% deprecated=1
Buffer createBufferFromUTF8(String str) {
#if PXT_UTF8
    auto sz = toRealUTF8(str, NULL);
    auto r = mkBuffer(NULL, sz);
    toRealUTF8(str, r->data);
    return r;
#else
    return mkBuffer((const uint8_t *)str->getUTF8Data(), str->getUTF8Size());
#endif
}
} // namespace control

namespace pxt {
static int writeBytes(uint8_t *dst, uint8_t *src, int length, bool swapBytes, int szLeft) {
    if (szLeft < length) {
        return -1;
    }

    if (swapBytes) {
        uint8_t *p = dst + length;
        for (int i = 0; i < length; ++i)
            *--p = src[i];
    } else {
        if (length == 4 && ((uintptr_t)dst & 3) == 0)
            *(uint32_t *)dst = *(uint32_t *)src;
        else if (length == 2 && ((uintptr_t)dst & 1) == 0)
            *(uint16_t *)dst = *(uint16_t *)src;
        else
            memcpy(dst, src, length);
    }

    return 0;
}

static int readBytes(uint8_t *src, uint8_t *dst, int length, bool swapBytes, int szLeft) {
    if (szLeft < length) {
        memset(dst, 0, length);
        return -1;
    }

    if (swapBytes) {
        uint8_t *p = src + length;
        for (int i = 0; i < length; ++i)
            dst[i] = *--p;
    } else {
        if (length == 4 && ((uintptr_t)src & 3) == 0)
            *(uint32_t *)dst = *(uint32_t *)src;
        else if (length == 2 && ((uintptr_t)src & 1) == 0)
            *(uint16_t *)dst = *(uint16_t *)src;
        else
            memcpy(dst, src, length);
    }

    return 0;
}

void setNumberCore(uint8_t *buf, int szLeft, NumberFormat format, TNumber value) {
    int8_t i8;
    uint8_t u8;
    int16_t i16;
    uint16_t u16;
    int32_t i32;
    uint32_t u32;
    float f32;
    double f64;

// Assume little endian
#define WRITEBYTES(isz, swap, toInt)                                                               \
    isz = toInt(value);                                                                            \
    writeBytes(buf, (uint8_t *)&isz, sizeof(isz), swap, szLeft);                                   \
    break

    switch (format) {
    case NumberFormat::Int8LE:
        WRITEBYTES(i8, false, toInt);
    case NumberFormat::UInt8LE:
        WRITEBYTES(u8, false, toInt);
    case NumberFormat::Int16LE:
        WRITEBYTES(i16, false, toInt);
    case NumberFormat::UInt16LE:
        WRITEBYTES(u16, false, toInt);
    case NumberFormat::Int32LE:
        WRITEBYTES(i32, false, toInt);
    case NumberFormat::UInt32LE:
        WRITEBYTES(u32, false, toUInt);

    case NumberFormat::Int8BE:
        WRITEBYTES(i8, true, toInt);
    case NumberFormat::UInt8BE:
        WRITEBYTES(u8, true, toInt);
    case NumberFormat::Int16BE:
        WRITEBYTES(i16, true, toInt);
    case NumberFormat::UInt16BE:
        WRITEBYTES(u16, true, toInt);
    case NumberFormat::Int32BE:
        WRITEBYTES(i32, true, toInt);
    case NumberFormat::UInt32BE:
        WRITEBYTES(u32, true, toUInt);

    case NumberFormat::Float32LE:
        WRITEBYTES(f32, false, toFloat);
    case NumberFormat::Float32BE:
        WRITEBYTES(f32, true, toFloat);
    case NumberFormat::Float64LE:
        WRITEBYTES(f64, false, toDouble);
    case NumberFormat::Float64BE:
        WRITEBYTES(f64, true, toDouble);
    }
}

TNumber getNumberCore(uint8_t *buf, int szLeft, NumberFormat format) {
    int8_t i8;
    uint8_t u8;
    int16_t i16;
    uint16_t u16;
    int32_t i32;
    uint32_t u32;
    float f32;
    double f64;

// Assume little endian
#define READBYTES(isz, swap, conv)                                                                 \
    readBytes(buf, (uint8_t *)&isz, sizeof(isz), swap, szLeft);                                    \
    return conv(isz)

    switch (format) {
    case NumberFormat::Int8LE:
        READBYTES(i8, false, fromInt);
    case NumberFormat::UInt8LE:
        READBYTES(u8, false, fromInt);
    case NumberFormat::Int16LE:
        READBYTES(i16, false, fromInt);
    case NumberFormat::UInt16LE:
        READBYTES(u16, false, fromInt);
    case NumberFormat::Int32LE:
        READBYTES(i32, false, fromInt);
    case NumberFormat::UInt32LE:
        READBYTES(u32, false, fromUInt);

    case NumberFormat::Int8BE:
        READBYTES(i8, true, fromInt);
    case NumberFormat::UInt8BE:
        READBYTES(u8, true, fromInt);
    case NumberFormat::Int16BE:
        READBYTES(i16, true, fromInt);
    case NumberFormat::UInt16BE:
        READBYTES(u16, true, fromInt);
    case NumberFormat::Int32BE:
        READBYTES(i32, true, fromInt);
    case NumberFormat::UInt32BE:
        READBYTES(u32, true, fromUInt);

    case NumberFormat::Float32LE:
        READBYTES(f32, false, fromFloat);
    case NumberFormat::Float32BE:
        READBYTES(f32, true, fromFloat);
    case NumberFormat::Float64LE:
        READBYTES(f64, false, fromDouble);
    case NumberFormat::Float64BE:
        READBYTES(f64, true, fromDouble);
    }

    return 0;
}
} // namespace pxt

base.buffer.ts:
namespace pins {
    //% deprecated=1
    export function sizeOf(format: NumberFormat) {
        return Buffer.sizeOfNumberFormat(format)
    }

    //% deprecated=1
    export function createBufferFromArray(bytes: number[]) {
        return Buffer.fromArray(bytes)
    }

    //% deprecated=1
    export function packedSize(format: string) {
        return Buffer.packedSize(format)
    }

    //% deprecated=1
    export function packBuffer(format: string, nums: number[]) {
        return Buffer.pack(format, nums)
    }

    //% deprecated=1
    export function packIntoBuffer(format: string, buf: Buffer, offset: number, nums: number[]) {
        buf.packAt(offset, format, nums)
    }

    //% deprecated=1
    export function unpackBuffer(format: string, buf: Buffer, offset = 0) {
        return buf.unpack(format, offset)
    }

    //% deprecated=1
    export function concatBuffers(bufs: Buffer[]) {
        return Buffer.concat(bufs)
    }
}

// see http://msgpack.org/ for the spec
// it currently only implements numbers and their sequances
// once we handle any type and typeof expressions we can do more

namespace msgpack {
    function tagFormat(tag: number) {
        switch (tag) {
            case 0xCB: return NumberFormat.Float64BE
            case 0xCC: return NumberFormat.UInt8BE
            case 0xCD: return NumberFormat.UInt16BE
            case 0xCE: return NumberFormat.UInt32BE
            case 0xD0: return NumberFormat.Int8BE
            case 0xD1: return NumberFormat.Int16BE
            case 0xD2: return NumberFormat.Int32BE
            default:
                return null
        }
    }

    function packNumberCore(buf: Buffer, offset: number, num: number) {
        let tag = 0xCB
        if (num == (num << 0) || num == (num >>> 0)) {
            if (-31 <= num && num <= 127) {
                if (buf) buf[offset] = num
                return 1
            } else if (0 <= num) {
                if (num <= 0xff) {
                    tag = 0xCC
                } else if (num <= 0xffff) {
                    tag = 0xCD
                } else {
                    tag = 0xCE
                }
            } else {
                if (-0x7f <= num) {
                    tag = 0xD0
                } else if (-0x7fff <= num) {
                    tag = 0xD1
                } else {
                    tag = 0xD2
                }
            }
        }
        let fmt = tagFormat(tag)
        if (buf) {
            buf[offset] = tag
            buf.setNumber(fmt, offset + 1, num)
        }
        return pins.sizeOf(fmt) + 1
    }

    /**
     * Unpacks a buffer into a number array.
     */
    export function unpackNumberArray(buf: Buffer, offset = 0): number[] {
        let res: number[] = []

        while (offset < buf.length) {
            let fmt = tagFormat(buf[offset++])
            if (fmt === null) {
                let v = buf.getNumber(NumberFormat.Int8BE, offset - 1)
                if (-31 <= v && v <= 127)
                    res.push(v)
                else
                    return null
            } else {
                res.push(buf.getNumber(fmt, offset))
                offset += pins.sizeOf(fmt)
            }
            // padding at the end
            while (buf[offset] === 0xc1) offset++;
        }

        return res
    }

    /**
     * Pack a number array into a buffer.
     * @param nums the numbers to be packed
     */
    export function packNumberArray(nums: number[]): Buffer {
        let off = 0
        for (let n of nums) {
            off += packNumberCore(null, off, n)
        }
        let buf = Buffer.create(off)
        off = 0
        for (let n of nums) {
            off += packNumberCore(buf, off, n)
        }
        return buf
    }
}

namespace helpers {
    export function bufferConcat(a: Buffer, b: Buffer) {
        const r = Buffer.create(a.length + b.length)
        r.write(0, a)
        r.write(a.length, b)
        return r
    }

    export function bufferEquals(l: Buffer, r: Buffer) {
        if (!l || !r) return !!l == !!r;
        if (l.length != r.length) return false;
        for (let i = 0; i < l.length; ++i) {
            if (l[i] != r[i])
                return false;
        }
        return true;
    }

    export function bufferIndexOf(a: Buffer, b: Buffer) {
        for (let i = 0; i <= a.length - b.length; ++i) {
            if (a[i] == b[0]) {
                let j = 0
                while (j < b.length) {
                    if (a[i + j] != b[j])
                        break
                    j++
                }
                if (j >= b.length)
                    return i
            }
        }
        return -1
    }

    export function bufferUnpack(buf: Buffer, format: string, offset?: number) {
        if (!offset) offset = 0
        let res: number[] = []
        Buffer.__packUnpackCore(format, res, buf, false, offset)
        return res
    }

    export function bufferPackAt(buf: Buffer, offset: number, format: string, nums: number[]) {
        Buffer.__packUnpackCore(format, nums, buf, true, offset)
    }

    export function bufferChunked(buf: Buffer, maxBytes: number) {
        if (buf.length <= maxBytes) return [buf]
        else {
            const r: Buffer[] = []
            for (let i = 0; i < buf.length; i += maxBytes)
                r.push(buf.slice(i, maxBytes))
            return r
        }
    }

    export function bufferToArray(buf: Buffer, format: NumberFormat) {
        const sz = Buffer.sizeOfNumberFormat(format)
        const len = buf.length - sz
        const r: number[] = []
        for (let i = 0; i <= len; i += sz)
            r.push(buf.getNumber(format, i))
        return r
    }

    export const _b64 = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/"
    export function bufferToBase64(buf: Buffer) {
        const len = buf.length
        let r = ""
        for (let i = 0; i < len; i += 3) {
            const x0 = buf[i]
            r += _b64[x0 >> 2]
            if (i + 1 >= len) {
                r += _b64[(x0 & 3) << 4] + "=="
            } else {
                const x1 = buf[i + 1]
                r += _b64[(x0 & 3) << 4 | (x1 >> 4)]
                if (i + 2 >= len) {
                    r += _b64[(x1 & 15) << 2] + "="
                } else {
                    const x2 = buf[i + 2]
                    r += _b64[(x1 & 15) << 2 | (x2 >> 6)]
                    r += _b64[x2 & 63]
                }
            }
        }
        return r
    }
}

interface Buffer {
    [index: number]: number;

    /**
     * Return concatenation of current buffer and the given buffer
     */
    //% helper=bufferConcat
    concat(other: Buffer): Buffer;

    /**
     * Return position of other buffer in current buffer
     */
    //% helper=bufferIndexOf
    indexOf(other: Buffer): number;

    /**
     * Reads numbers from the buffer according to the format
     */
    //% helper=bufferUnpack
    unpack(format: string, offset?: number): number[];

    /**
     * Writes numbers to the buffer according to the format
     */
    //% helper=bufferPackAt
    packAt(offset: number, format: string, nums: number[]): void;

    /**
     * Returns true if this and the other buffer hold the same data
     */
    //% helper=bufferEquals
    equals(other: Buffer): boolean;

    /**
     * Splits buffer into parts no larger than specified
     */
    //% helper=bufferChunked
    chunked(maxSize: number): Buffer[];

    /**
     * Read contents of buffer as an array in specified format
     */
    //% helper=bufferToArray
    toArray(format: NumberFormat): number[];

    /**
     * Convert buffer to ASCII base64 encoding.
     */
    //% helper=bufferToBase64
    toBase64(): string;

    // rest defined in buffer.cpp
}

namespace Buffer {
    /**
     * Allocate a new buffer.
     * @param size number of bytes in the buffer
     */
    //% shim=control::createBuffer
    export declare function create(size: number): Buffer;

    /**
     * Create a new buffer, decoding a hex string
     */
    export function fromHex(hex: string) {
        const hexStr = "0123456789abcdef"
        const res = Buffer.create(hex.length >> 1)
        hex = hex.toLowerCase()
        for (let i = 0; i < hex.length; i += 2) {
            const p0 = hexStr.indexOf(hex.charAt(i))
            const p1 = hexStr.indexOf(hex.charAt(i + 1))
            if (p0 < 0 || p1 < 0)
                throw "Invalid hex"
            res[i >> 1] = (p0 << 4) | p1
        }
        return res
    }

    function b64Idx(c: string) {
        if (c === undefined || c == "=") return -1

        // handle base64url
        if (c == "-") return 62
        if (c == "_") return 63

        const r = helpers._b64.indexOf(c)
        if (r < 0)
            throw "Invalid Base64"
        return r
    }

    function fromBase64Core(trg: Buffer, b64: string) {
        const len = b64.length
        let dp = 0
        for (let i = 0; i < len; i += 4) {
            const x0 = b64Idx(b64[i])
            const x1 = b64Idx(b64[i + 1])
            const x2 = b64Idx(b64[i + 2])
            const x3 = b64Idx(b64[i + 3])
            if (x0 < 0 || x1 < 0) throw "Invalid Base64"
            if (trg)
                trg[dp] = (x0 << 2) | (x1 >> 4)
            dp++
            if (x2 >= 0) {
                if (trg)
                    trg[dp] = (x1 << 4) | (x2 >> 2)
                dp++
                if (x3 >= 0) {
                    if (trg)
                        trg[dp] = (x2 << 6) | x3
                    dp++
                }
            } else {
                if (x3 >= 0 || i + 4 < len)
                    throw "Invalid Base64"
            }
        }
        return dp
    }

    /**
     * Create a new buffer, decoding a Base64 string
     */
    export function fromBase64(b64: string) {
        const sz = fromBase64Core(null, b64)
        const res = create(sz)
        fromBase64Core(res, b64)
        return res
    }

    /**
     * Create a new buffer from an UTF8-encoded string
     * @param str the string to put in the buffer
     */
    //% shim=control::createBufferFromUTF8
    export declare function fromUTF8(str: string): Buffer;

    function chunkLen(s: string, off: number, maxlen: number) {
        let L = Math.idiv(maxlen, 3)
        let R = maxlen

        if (fromUTF8(s.slice(off, off + R)).length <= maxlen)
            return R

        while (L < R) {
            const m = (L + R) >> 1
            if (m == L)
                break
            const ll = fromUTF8(s.slice(off, off + m)).length
            if (ll <= maxlen)
                L = m
            else
                R = m
        }

        return L
    }

    export function chunkedFromUTF8(str: string, maxBytes: number) {
        if (maxBytes < 3)
            throw "Oops"
        const chunks: Buffer[] = []
        let pos = 0
        while (pos < str.length) {
            const len = chunkLen(str, pos, maxBytes)
            chunks.push(fromUTF8(str.slice(pos, pos + len)))
            pos += len
        }
        return chunks
    }

    /**
     * Create a new buffer initialized to bytes from given array.
     * @param bytes data to initialize with
     */
    export function fromArray(bytes: number[]) {
        let buf = Buffer.create(bytes.length)
        for (let i = 0; i < bytes.length; ++i)
            buf[i] = bytes[i]
        return buf
    }

    /**
     * Concatenates all buffers in the list
     */
    export function concat(buffers: Buffer[]) {
        let len = 0
        for (let b of buffers)
            len += b.length
        const r = Buffer.create(len)
        len = 0
        for (let b of buffers) {
            r.write(len, b)
            len += b.length
        }
        return r
    }

    // Python-like packing, see https://docs.python.org/3/library/struct.html

    export function packedSize(format: string) {
        return __packUnpackCore(format, null, null, true)
    }

    export function pack(format: string, nums: number[]) {
        let buf = Buffer.create(packedSize(format))
        __packUnpackCore(format, nums, buf, true)
        return buf
    }

    function getFormat(pychar: string, isBig: boolean) {
        switch (pychar) {
            case 'B':
                return NumberFormat.UInt8LE
            case 'b':
                return NumberFormat.Int8LE
            case 'H':
                return isBig ? NumberFormat.UInt16BE : NumberFormat.UInt16LE
            case 'h':
                return isBig ? NumberFormat.Int16BE : NumberFormat.Int16LE
            case 'I':
            case 'L':
                return isBig ? NumberFormat.UInt32BE : NumberFormat.UInt32LE
            case 'i':
            case 'l':
                return isBig ? NumberFormat.Int32BE : NumberFormat.Int32LE
            case 'f':
                return isBig ? NumberFormat.Float32BE : NumberFormat.Float32LE
            case 'd':
                return isBig ? NumberFormat.Float64BE : NumberFormat.Float64LE
            default:
                return null as NumberFormat
        }
    }

    function isDigit(ch: string) {
        const code = ch.charCodeAt(0)
        return 0x30 <= code && code <= 0x39
    }

    export function __packUnpackCore(format: string, nums: number[], buf: Buffer, isPack: boolean, off = 0) {
        let isBig = false
        let idx = 0
        for (let i = 0; i < format.length; ++i) {
            switch (format[i]) {
                case ' ':
                case '<':
                case '=':
                    isBig = false
                    break
                case '>':
                case '!':
                    isBig = true
                    break
                default:
                    const i0 = i
                    while (isDigit(format[i])) i++
                    let reps = 1
                    if (i0 != i)
                        reps = parseInt(format.slice(i0, i))
                    if (format[i] == 'x')
                        off += reps
                    else
                        while (reps--) {
                            let fmt = getFormat(format[i], isBig)
                            if (fmt === null) {
                                control.fail("Unsupported format character: " + format[i])
                            } else {
                                if (buf) {
                                    if (isPack)
                                        buf.setNumber(fmt, off, nums[idx++])
                                    else
                                        nums.push(buf.getNumber(fmt, off))
                                }

                                off += sizeOfNumberFormat(fmt)
                            }
                        }
                    break
            }
        }
        return off
    }

    /**
     * Get the size in bytes of specified number format.
     */
    export function sizeOfNumberFormat(format: NumberFormat) {
        switch (format) {
            case NumberFormat.Int8LE:
            case NumberFormat.UInt8LE:
            case NumberFormat.Int8BE:
            case NumberFormat.UInt8BE:
                return 1;
            case NumberFormat.Int16LE:
            case NumberFormat.UInt16LE:
            case NumberFormat.Int16BE:
            case NumberFormat.UInt16BE:
                return 2;
            case NumberFormat.Int32LE:
            case NumberFormat.Int32BE:
            case NumberFormat.UInt32BE:
            case NumberFormat.UInt32LE:
            case NumberFormat.Float32BE:
            case NumberFormat.Float32LE:
                return 4;
            case NumberFormat.Float64BE:
            case NumberFormat.Float64LE:
                return 8;
        }
        return 0;
    }
}

