namespace Math_ {
//%
TNumber pow(TNumber x, TNumber y) {
#ifdef PXT_POWI
    // regular pow() from math.h is 4k of code, but it can
    // also be expressed as exp(y * log(x)) which is less
    // performant than pow() but doesn't increase code size
    double dx = toDouble(x);
    double dy = toDouble(y);

    // shortcut to integer pow if y is an integer
    if (::floor(dy) == dy) {
        return fromDouble(__builtin_powi(dx, dy));
    }
    if (dx > 0) {
        return fromDouble(::exp(dy * ::log(dx)));
    }
    if (dx == 0) {
        if (dy < 0) {
            // positive infinity
            return fromDouble(HUGE_VAL);
        }
        return x;
    }
    return fromDouble(::log(dx));
#else
    return fromDouble(::pow(toDouble(x), toDouble(y)));
#endif
}

NUMBER randomDouble() {
    return getRandom(UINT_MAX) / ((NUMBER)UINT_MAX + 1) +
           getRandom(0xffffff) / ((NUMBER)UINT_MAX * 0xffffff);
}

//%
TNumber random() {
    return fromDouble(randomDouble());
}

//%
TNumber randomRange(TNumber min, TNumber max) {
    if (isInt(min) && isInt(max)) {
        int mini = numValue(min);
        int maxi = numValue(max);
        if (mini > maxi) {
            int temp = mini;
            mini = maxi;
            maxi = temp;
        }
        if (maxi == mini)
            return fromInt(mini);
        else
            return fromInt(mini + getRandom(maxi - mini));
    } else {
        auto mind = toDouble(min);
        auto maxd = toDouble(max);
        if (mind > maxd) {
            auto temp = mind;
            mind = maxd;
            maxd = temp;
        }
        if (maxd == mind)
            return fromDouble(mind);
        else {
            return fromDouble(mind + randomDouble() * (maxd - mind));
        }
    }
}

#define SINGLE(op) return fromDouble(::op(toDouble(x)));

