        fiber_sleep(20);
    }
}

void runForever(Action a) {
    if (a != 0) {
        registerGCPtr(a);
        create_fiber(forever_stub, (void *)a);
    }
}

void runInParallel(Action a) {
    if (a != 0) {
        registerGCPtr(a);
        create_fiber((void (*)(void *))(void*)runAction0, (void *)a, fiberDone);
    }
}

