core.i2c.cpp:
#include "pxt.h"
#include "ErrorNo.h"
#include "CodalDmesg.h"
#include "configkeys.h"

#ifdef CODAL_I2C

namespace pins {

class CodalI2CProxy {
private:
  DevicePin* sda;
  DevicePin* scl;
  CODAL_I2C i2c;
public:
  CodalI2CProxy* next;
public:
  CodalI2CProxy(DevicePin* _sda, DevicePin* _scl)
    : sda(_sda)
    , scl(_scl)
    , i2c(*_sda, *_scl) 
    , next(NULL)
  {

  }

  CODAL_I2C* getI2C() {
    return &(this->i2c);
  }

  bool matchPins(DevicePin* sda, DevicePin* scl) {
      return this->sda == sda && this->scl == scl;
  }

  Buffer readBuffer(int address, int size, bool repeat = false)
  {
    Buffer buf = mkBuffer(NULL, size);
    registerGCObj(buf);
    int status = this->i2c.read(address << 1, buf->data, size, repeat);
    unregisterGCObj(buf);
    if (status != ErrorCode::DEVICE_OK) {
      buf = 0;
    }
    return buf;
  }

  int writeBuffer(int address, Buffer buf, bool repeat = false)
  {
    return this->i2c.write(address << 1, buf->data, buf->length, repeat);
  }
};

}

namespace I2CMethods {
/**
  * Read `size` bytes from a 7-bit I2C `address`.
  */
//%
Buffer readBuffer(I2C_ i2c, int address, int size, bool repeat = false)
{
  return i2c->readBuffer(address, size, repeat);
}

/**
  * Write bytes to a 7-bit I2C `address`.
  */
//%
int writeBuffer(I2C_ i2c, int address, Buffer buf, bool repeat = false)
{
  return i2c->writeBuffer(address, buf, repeat);
}

}

namespace pins {

static I2C_ i2cs(NULL);
/**
* Opens a Serial communication driver
*/
//% help=pins/create-i2c
//% parts=i2c
I2C_ createI2C(DigitalInOutPin sda, DigitalInOutPin scl) {
  // pick up defaults
  if (!sda || !scl) {
    DMESG("i2c: lookup default pins");
    sda = LOOKUP_PIN(SDA);
    scl = LOOKUP_PIN(SCL);
  }

  // lookup existing devices
  auto dev = i2cs;
  while(dev) {
    if (dev->matchPins(sda, scl)) {
      DMESG("i2c: found existing i2c");
      return dev;
    }
    dev = dev->next;
  }

  // allocate new one
  DMESG("i2c: mounting on new device");
  auto ser = new CodalI2CProxy(sda, scl);
  // push in list
  ser->next = i2cs;
  i2cs = ser;
  return ser;
}

}

namespace pxt {
  CODAL_I2C* getI2C(DigitalInOutPin sda, DigitalInOutPin scl) {
    auto i2c = pins::createI2C(sda, scl);
    return i2c->getI2C();
  }
}

#endif

core.i2c.ts:
namespace pins {
    /**
     * Read one number from an I2C address.
     */
    //% help=pins/i2c-read-number weight=5 group="i2c" inlineInputMode="external"
    //% blockId=pins_i2c_readnumber block="i2c read number at address %address|of format %format|repeated %repeated"
    export function i2cReadNumber(address: number, format: NumberFormat, repeated?: boolean): number {
        const buf = pins.i2cReadBuffer(address, pins.sizeOf(format), repeated)
        if (!buf)
            return undefined
        return buf.getNumber(format, 0)
    }

    /**
     * Write one number to an I2C address.
     */
    //% help=pins/i2c-write-number weight=4 group="i2c"
    //% blockId=i2c_writenumber block="i2c write number|at address %address|with value %value|of format %format|repeated %repeated"
    export function i2cWriteNumber(address: number, value: number, format?: NumberFormat, repeated?: boolean): void {
        if (format == undefined)
            format = NumberFormat.UInt8LE;
        const buf = control.createBuffer(pins.sizeOf(format))
        buf.setNumber(format, 0, value)
        pins.i2cWriteBuffer(address, buf, repeated)
    }

    /**
     * Write a value in a I2C register.
     * @param address I2c address of the device
     * @param register register index
     * @param value value to write
     * @param valueFormat format of the value, default is UInt8LE
     */
    //% weight=3 group="i2c"
    //% help=pins/i2c-write-register
    //% blockId=i2c_writereg block="i2c write register|at address $address|at register $register|value $value"
    export function i2cWriteRegister(address: number, register: number, value: number, valueFormat?: NumberFormat): void {
        if (valueFormat === undefined)
            valueFormat = NumberFormat.UInt8LE;
        const valueSize = pins.sizeOf(valueFormat);
        const buf = control.createBuffer(1 + valueSize);
        buf.setNumber(NumberFormat.UInt8LE, 0, register);
        buf.setNumber(valueFormat, 1, value);
        pins.i2cWriteBuffer(address, buf);
    }

    /**
     * Read the value from a I2C register.
     * @param address I2c address of the device
     * @param register register index
     * @param valueFormat format of the value, default is UInt8LE
     */
    //% weight=3 group="i2c"
    //% help=pins/i2c-read-register
    //% blockId=i2c_readreg block="i2c read register|at address $address|at register $register"
    export function i2cReadRegister(address: number, register: number, valueFormat?: NumberFormat): number {
        if (valueFormat === undefined)
            valueFormat = NumberFormat.UInt8LE;
        pins.i2cWriteNumber(address, register, NumberFormat.UInt8LE);
        return pins.i2cReadNumber(address, valueFormat);
    }

    /**
     * Read `size` bytes from a 7-bit I2C `address`.
     */
    //%
    export function i2cReadBuffer(address: number, size: number, repeat: boolean = false): Buffer {
        return pins.i2c().readBuffer(address, size, repeat);
    }

    /**
     * Write bytes to a 7-bit I2C `address`.
     */
    //%
    export function i2cWriteBuffer(address: number, buf: Buffer, repeat: boolean = false): number {
        return pins.i2c().writeBuffer(address, buf, repeat);
    }

    let _i2c: I2C;
    /**
     * Gets the default I2C bus
     */
    //%
    export function i2c(): I2C {
        if (!_i2c) {
            const sda = pins.pinByCfg(DAL.CFG_PIN_SDA);
            const scl = pins.pinByCfg(DAL.CFG_PIN_SCL);
            _i2c = pins.createI2C(sda, scl);    
        }
        return _i2c;        
    }

    export class I2CDevice {
        public address: number;
        public bus: I2C;
        private _hasError: boolean;
        constructor(address: number, bus?: I2C) {
            this.address = address;
            this.bus = bus || i2c();
        }
        public readInto(buf: Buffer, repeat = false, start = 0, end: number = null) {
            if (end === null)
                end = buf.length
            if (start >= end)
                return
            let res = this.bus.readBuffer(this.address, end - start, repeat)
            if (!res) {
                this._hasError = true
                return
            }
            buf.write(start, res)
        }
        public write(buf: Buffer, repeat = false) {
            let res = this.bus.writeBuffer(this.address, buf, repeat)
            if (res) {
                this._hasError = true
            }
        }
        public begin(): I2CDevice {
            this._hasError = false;
            return this;
        }
        public end() {
        }
        public ok() {
            return !this._hasError
        }

        public transfer(command: Buffer, response: Buffer, responseStart?: number, responseEnd?: number) {
            this.begin();
            if (command)
                this.write(command);
            if (response)
                this.readInto(response, false, responseStart, responseEnd);
            this.end();
        }    
    }
}

core.leveldetector.ts:
namespace pins {
    export class LevelDetector {
        public id: number;
        public min: number;
        public max: number;
        public lowThreshold: number;
        public highThreshold: number;
        private transition: number;
        private transitionMs: number;
        private _level: number;
        private _state: number;
        public onHigh: () => void;
        public onLow: () => void;
        public onNeutral: () => void;
        public transitionWindow: number;
        // minimum duration (ms) between events
        public transitionInterval: number;

        static LEVEL_THRESHOLD_NEUTRAL = 0;

        constructor(id: number,
            min: number, max: number,
            lowThreshold: number, highThreshold: number) {
            this.id = id;
            this.min = min;
            this.max = max;
            this.lowThreshold = lowThreshold;
            this.highThreshold = highThreshold;
            this.transitionWindow = 4;
            this.transitionInterval = 0;

            this.onHigh = () => control.raiseEvent(this.id, DAL.LEVEL_THRESHOLD_HIGH);
            this.onLow = () => control.raiseEvent(this.id, DAL.LEVEL_THRESHOLD_LOW);
            this.onNeutral = undefined;

            this.reset();
        }

        reset() {
            this.transition = 0;
            this.transitionMs = 0;
            this._level = Math.ceil((this.highThreshold - this.lowThreshold) / 2);
            this._state = LevelDetector.LEVEL_THRESHOLD_NEUTRAL;
        }

        get level(): number {
            return this._level;
        }

        set level(level: number) {
            this._level = this.clampValue(level);

            if (this._level >= this.highThreshold) {
                this.setState(DAL.LEVEL_THRESHOLD_HIGH);
            }
            else if (this._level <= this.lowThreshold) {
                this.setState(DAL.LEVEL_THRESHOLD_LOW);
            }
            else {
                this.setState(LevelDetector.LEVEL_THRESHOLD_NEUTRAL);
            }
        }

        public setLowThreshold(value: number) {
            this.lowThreshold = this.clampValue(value);
            this.reset();
        }

        public setHighThreshold(value: number) {
            this.highThreshold = this.clampValue(value);
            this.reset();
        }

        private clampValue(value: number) {
            if (value < this.min) {
                return this.min;
            }
            else if (value > this.max) {
                return this.max;
            }
            return value;
        }

        private setState(state: number) {
            // not enough samples to change
            if (this._state === state 
                || (this.transition++ < this.transitionWindow)
                || (control.millis() - this.transitionMs) < this.transitionInterval) {
                return;
            }

            this.transition = 0;
            this.transitionMs = control.millis();
            this._state = state;
            switch (state) {
                case DAL.LEVEL_THRESHOLD_HIGH:
                    if (this.onHigh) this.onHigh();
                    break;
                case DAL.LEVEL_THRESHOLD_LOW:
                    if (this.onLow) this.onLow();
                    break;
                case LevelDetector.LEVEL_THRESHOLD_NEUTRAL:
                    if (this.onNeutral) this.onNeutral();
                    break;
            }
        }
    }
}

core.light.cpp:
#include "light.h"

// WS2812B timings, datasheet v1
// 0 - 0.25-0.55us hi 0.70-1.00us low
// 1 - 0.65-0.95us hi 0.30-0.60us low
// datasheet v5
// 0 - 0.22-0.38us hi 0.58-1.00us low 1-3
// 1 - 0.58-1.00us hi 0.58-1.00us low 3-2

#define SPI_SUPPORTED 1

#if defined(NRF52_SERIES)
#define BIT_EXPANSION 5
#define SPI_FREQ 4000000
#else
#define BIT_EXPANSION 3
#define SPI_FREQ 2400000
#endif

#if defined(SAMD21) || defined(SAMD51) || defined(STM32F4) || defined(NRF52_SERIES)
#include "neopixel.h"
#define BITBANG_SUPPORTED 1
#else
#define BITBANG_SUPPORTED 0
#endif

#define NEOPIXEL_MIN_LENGTH_FOR_SPI 24
#define DOTSTAR_MIN_LENGTH_FOR_SPI 24

#define LIGHTMODE_RGB 1
#define LIGHTMODE_RGBW 2
#define LIGHTMODE_RGB_RGB 3
#define LIGHTMODE_DOTSTAR 4

namespace light {
bool isValidMOSIPin(DigitalInOutPin pin) {
    if (!pin)
        return false;

#if SAMD51
    return ZSPI::isValidMOSIPin(*pin);
#elif defined(NRF52_SERIES)
    return true;
#else
    // TODO: support for SPI neopixels
    // default SPI pins supported for now
    return pin == LOOKUP_PIN(MOSI);
#endif
}

// SPI
void spiNeopixelSendBuffer(DevicePin *pin, const uint8_t *data, unsigned size) {
    int32_t iptr = 0, optr = 120;
    uint32_t len = optr + size * BIT_EXPANSION + 120;
    uint8_t *expBuf = new uint8_t[len];
    memset(expBuf, 0, len);
    uint8_t imask = 0x80;
    uint8_t omask = 0x80;

#define WR(k)                                                                                      \
    if (k)                                                                                         \
        expBuf[optr] |= omask;                                                                     \
    omask >>= 1;                                                                                   \
    if (!omask) {                                                                                  \
        omask = 0x80;                                                                              \
        optr++;                                                                                    \
    }

    while (iptr < (int)size) {
#if BIT_EXPANSION == 3
        WR(1);
        WR(data[iptr] & imask);
        WR(0);
#elif BIT_EXPANSION == 5
        WR(1);
        if (data[iptr] & imask) {
            WR(1);
            WR(1);
        } else {
            WR(0);
        }
        WR(0);
        WR(0);
#else
#error "invalid BIT_EXPANSION"
#endif

        imask >>= 1;
        if (!imask) {
            imask = 0x80;
            iptr++;
        }
    }

    auto spi = pxt::getSPI(pin, NULL, NULL);
    spi->setFrequency(SPI_FREQ);
    spi->transfer(expBuf, len, NULL, 0);
    delete expBuf;
}

void neopixelSendData(DevicePin *pin, int mode, const uint8_t *data, unsigned length) {
    if (!pin || !length)
        return;

#if BITBANG_SUPPORTED
    if (SPI_SUPPORTED && length > NEOPIXEL_MIN_LENGTH_FOR_SPI && isValidMOSIPin(pin))
        spiNeopixelSendBuffer(pin, data, length);
    else
        neopixel_send_buffer(*pin, data, length);
#else
    if (isValidMOSIPin(pin)) {
        spiNeopixelSendBuffer(pin, data, length);
    }
#endif
}

void bitBangDotStarSendData(DevicePin *data, DevicePin *clk, int mode, const uint8_t *buf,
                            unsigned length) {
    // first frame of zeroes
    data->setDigitalValue(0);
    for (unsigned i = 0; i < 32; ++i) {
        clk->setDigitalValue(1);
        clk->setDigitalValue(0);
    }

    // data stream
    for (unsigned i = 0; i < length; ++i) {
        auto x = buf[i];
        for (uint8_t j = 0x80; j != 0; j >>= 1) {
            data->setDigitalValue(x & j ? 1 : 0);
            clk->setDigitalValue(1);
            clk->setDigitalValue(0);
        }
    }
    // https://cpldcpu.wordpress.com/2016/12/13/sk9822-a-clone-of-the-apa102/
    // reset frame
    // data->setDigitalValue(0);
    // for (unsigned i = 0; i < 32 ; ++i) {
    //    clk->setDigitalValue(1);
    //    clk->setDigitalValue(0);
    //}

    // https://cpldcpu.wordpress.com/2014/11/30/understanding-the-apa102-superled/
    data->setDigitalValue(1);
    unsigned n = 32;
    for (unsigned i = 0; i < n; ++i) {
        clk->setDigitalValue(1);
        clk->setDigitalValue(0);
    }
}

static uint8_t ZERO_FRAME[4];
static uint8_t ONE_FRAME[] = {1, 1, 1, 1};
void spiDotStarSendData(DevicePin *data, DevicePin *clk, int mode, const uint8_t *buf,
                        unsigned length) {
    auto spi = pxt::getSPI(data, NULL, clk);

    spi->transfer(ZERO_FRAME, sizeof(ZERO_FRAME), NULL, 0); // initial frame
    spi->transfer(buf, length, NULL, 0);
    spi->transfer(ZERO_FRAME, sizeof(ZERO_FRAME), NULL, 0); // reset frame
    for (unsigned i = 0; i < (length >> 3); i += 32)
        spi->transfer(ONE_FRAME, sizeof(ONE_FRAME), NULL, 0); // final frame
}

void dotStarSendData(DevicePin *data, DevicePin *clk, int mode, const uint8_t *buf,
                     unsigned length) {
    if (!data || !clk || !buf || !length)
        return;

    if (length > DOTSTAR_MIN_LENGTH_FOR_SPI && isValidMOSIPin(data))
        spiDotStarSendData(data, clk, mode, buf, length);
    else
        bitBangDotStarSendData(data, clk, mode, buf, length);
}

void sendBuffer(DevicePin *data, DevicePin *clk, int mode, Buffer buf) {
    if (!data || !buf || !buf->length)
        return;

    if (mode == LIGHTMODE_DOTSTAR)
        light::dotStarSendData(data, clk, mode, buf->data, buf->length);
    else
        light::neopixelSendData(data, mode, buf->data, buf->length);
}

void clear() {
    auto neopix = LOOKUP_PIN(NEOPIXEL);
    auto neonum = getConfig(CFG_NUM_NEOPIXELS, 0);
    if (neopix && neonum >= 0) {
        auto n = 3 * neonum;
        uint8_t off[n];
        memset(off, 0, sizeof(off));
        light::neopixelSendData(neopix, 0x100, off, sizeof(off));
    }

    auto data = LOOKUP_PIN(DOTSTAR_DATA);
    auto clk = LOOKUP_PIN(DOTSTAR_CLOCK);
    auto dsnum = getConfig(CFG_NUM_DOTSTARS, 0);
    if (data && clk && dsnum > 0) {
        auto n = 4 * dsnum;
        uint8_t off[n];
        memset(off, 0, sizeof(off));
        for (int i = 0; i < n; i += 4)
            off[i] = 0xe0;
        bitBangDotStarSendData(data, clk, 0x100, off, sizeof(off));
    }
}

} // namespace light

