int HF2::stdRequest(UsbEndpointIn &ctrl, USBSetup &setup) {
#ifdef HF2_HID
    if (!useHID)
        return DEVICE_NOT_SUPPORTED;
    if (setup.bRequest == USB_REQ_GET_DESCRIPTOR) {
