            fillInterfaceInfo(&tmp);
            return ctrl.write(&tmp, sizeof(tmp));
        } else if (setup.wValueH == 0x22) {
            return ctrl.write(hidDescriptor, sizeof(hidDescriptor));
        }
    }
#endif
