namespace control {
    /**
     * A timer
     */
    //% fixedInstances
    export class Timer {
        start: number;

        constructor() {
            this.start = control.millis();
        }

        /**
         * Gets the elapsed time in millis since the last reset
         */
        //% blockId=timerMillis block="%timer|millis"
        millis(): number {
            return control.millis() - this.start;
        }

        /**
         * Gets the elapsed time in seconds since the last reset
         */
        //% blockId=timerSeconds block="%timer|seconds"
        seconds(): number {
            return this.millis() / 1000;
        }

        /**
         * Resets the timer
         */
        //% blockId=timerRest block="%timer|reset"
        reset() {
            this.start = control.millis();
        }

        /**
         * Pauses until the timer reaches the given amount of milliseconds
         * @param ms how long to pause for, eg: 5, 100, 200, 500, 1000, 2000
         */
        //% blockId=timerPauseUntil block="%timer|pause until (ms) %ms"
        pauseUntil(ms: number) {
            const remaining = this.millis() - ms;
            pause(Math.max(0, remaining));
        }
    }

    //% whenUsed fixedInstance block="timer 1"
    export const timer1 = new Timer();
    //% whenUsed fixedInstance block="timer 2"
    export const timer2 = new Timer();
    //% whenUsed fixedInstance block="timer 3"
    export const timer3 = new Timer();
    //% whenUsed fixedInstance block="timer 4"
    export const timer4 = new Timer();
    //% whenUsed fixedInstance block="timer 5"
    export const timer5 = new Timer();
    //% whenUsed fixedInstance block="timer 6"
    export const timer6 = new Timer();
    //% whenUsed fixedInstance block="timer 7"
    export const timer7 = new Timer();
    //% whenUsed fixedInstance block="timer 8"
    export const timer8 = new Timer();
}

core---samd.uf2format.h:
#ifndef UF2FORMAT_H
#define UF2FORMAT_H 1

#include <stdint.h>
#include <stdbool.h>

// All entries are little endian.

#ifndef BOOTLOADER_START
#define BOOTLOADER_START 0x0
#endif

#ifndef BOOTLOADER_END
#define BOOTLOADER_END 0x2000
#endif

#ifndef UF2_BINFO
#define UF2_BINFO ((UF2_BInfo *)(BOOTLOADER_END - sizeof(UF2_BInfo)))
#endif

#ifndef UF2_INFO_TXT
#define UF2_INFO_TXT UF2_BINFO->info_uf2
#endif


#define UF2_MAGIC_START0 0x0A324655UL // "UF2\n"
#define UF2_MAGIC_START1 0x9E5D5157UL // Randomly selected
#define UF2_MAGIC_END 0x0AB16F30UL    // Ditto

// If set, the block is "comment" and should not be flashed to the device
#define UF2_FLAG_NOFLASH 0x00000001

typedef struct {
    // 32 byte header
    uint32_t magicStart0;
    uint32_t magicStart1;
    uint32_t flags;
    uint32_t targetAddr;
    uint32_t payloadSize;
    uint32_t blockNo;
    uint32_t numBlocks;
    uint32_t reserved;

    // raw data;
    uint8_t data[476];

    // store magic also at the end to limit damage from partial block reads
    uint32_t magicEnd;
} UF2_Block;

typedef struct {
    uint8_t version;
    uint8_t ep_in;
    uint8_t ep_out;
    uint8_t reserved0;
    uint32_t cbw_tag;
    uint32_t blocks_remaining;
    uint8_t *buffer;
} UF2_HandoverArgs;

typedef void (*UF2_MSC_Handover_Handler)(UF2_HandoverArgs *handover);
typedef void (*UF2_HID_Handover_Handler)(int ep);

// this is required to be exactly 16 bytes long by the linker script
typedef struct {
    void *reserved0;
    UF2_HID_Handover_Handler handoverHID;
    UF2_MSC_Handover_Handler handoverMSC;
    const char *info_uf2;
} UF2_BInfo;

static inline bool is_uf2_block(void *data) {
    UF2_Block *bl = (UF2_Block *)data;
    return bl->magicStart0 == UF2_MAGIC_START0 && bl->magicStart1 == UF2_MAGIC_START1 &&
           bl->magicEnd == UF2_MAGIC_END;
}

static inline bool in_uf2_bootloader_space(const void *addr) {
    return (BOOTLOADER_START + 0x100) <= (uint32_t)addr && (uint32_t)addr < (BOOTLOADER_END);
}

static inline const char *uf2_info(void) {
    if (in_uf2_bootloader_space(UF2_INFO_TXT))
        return UF2_INFO_TXT;
    return "N/A";
}

#ifdef UF2_DEFINE_HANDOVER
static inline void hf2_handover(uint8_t ep) {
    const char *board_info = UF2_BINFO->info_uf2;
    UF2_HID_Handover_Handler fn = UF2_BINFO->handoverHID;

    if (in_uf2_bootloader_space(board_info) && in_uf2_bootloader_space((const void *)fn) &&
        ((uint32_t)fn & 1)) {
        // Pass control to bootloader; never returns
        fn(ep & 0xf);
    }
}

static inline void check_uf2_handover(uint8_t *buffer, uint32_t blocks_remaining, uint8_t ep_in,
                                      uint8_t ep_out, uint32_t cbw_tag) {
    if (!is_uf2_block(buffer))
        return;

    const char *board_info = UF2_BINFO->info_uf2;
    UF2_MSC_Handover_Handler fn = UF2_BINFO->handoverMSC;

    if (in_uf2_bootloader_space(board_info) && in_uf2_bootloader_space((const void *)fn) &&
        ((uint32_t)fn & 1)) {
        UF2_HandoverArgs hand = {
            1, ep_in, ep_out, 0, cbw_tag, blocks_remaining, buffer,
        };
        // Pass control to bootloader; never returns
        fn(&hand);
    }
}
#endif

#endif

