game.spritemap.ts:
namespace sprites {
    export class SpriteMap {
        private cellWidth: number;
        private cellHeight: number;
        private rowCount: number;
        private columnCount: number;
        private buckets: Sprite[][];
        filledBuckets: Sprite[][];

        constructor() {
            this.buckets = [];
        }

        /**
         * Returns a potential list of neighbors
         */
        neighbors(sprite: Sprite): Sprite[] {
            const n: Sprite[] = [];
            const layer = sprite.layer;
            this.mergeAtKey(sprite.left, sprite.top, layer, n)
            this.mergeAtKey(sprite.left, sprite.bottom, layer, n)
            this.mergeAtKey(sprite.right, sprite.top, layer, n)
            this.mergeAtKey(sprite.right, sprite.bottom, layer, n)
            n.removeElement(sprite);
            return n;
        }

        /**
         * Gets the overlaping sprites if any
         * @param sprite
         */
        overlaps(sprite: Sprite): Sprite[] {
            const n = this.neighbors(sprite);
            const o = n.filter(neighbor => sprite.overlapsWith(neighbor));
            return o;
        }

        draw() {
            for (let x = 0; x < this.columnCount; ++x) {
                for (let y = 0; y < this.rowCount; ++y) {
                    const left = x * this.cellWidth;
                    const top = y * this.cellHeight;
                    const k = this.key(left, top);
                    const b = this.buckets[k];
                    if (b && b.length)
                        screen.drawRect(left, top, this.cellWidth, this.cellHeight, 5);
                }
            }
        }

        /**
         * Recompute hashes for all objects
         */
        resizeBuckets(sprites: Sprite[]) {
            // rescale buckets
            let maxWidth = 0;
            let maxHeight = 0;
            for (const sprite of sprites) {
                if (sprite.width > maxWidth) maxWidth = sprite.width;
                if (sprite.height > maxHeight) maxHeight = sprite.height;
            }

            const tMap = game.currentScene().tileMap;

            const areaWidth = tMap ? tMap.areaWidth() : screen.width;
            const areaHeight = tMap ? tMap.areaHeight() : screen.height;

            this.cellWidth = Math.clamp(8, areaWidth >> 2, maxWidth << 1);
            this.cellHeight = Math.clamp(8, areaHeight >> 2, maxHeight << 1);
            this.rowCount = Math.idiv(areaHeight, this.cellHeight);
            this.columnCount = Math.idiv(areaWidth, this.cellWidth);
        }

        clear() {
            this.buckets = [];
            this.filledBuckets = [];
        }

        private key(x: number, y: number): number {
            const xi = Math.clamp(0, this.columnCount, Math.idiv(x, this.cellWidth));
            const yi = Math.clamp(0, this.rowCount, Math.idiv(y, this.cellHeight));
            return xi + yi * this.columnCount;
        }

        private insertAtKey(x: number, y: number, sprite: Sprite) {
            const k = this.key(x, y);
            let bucket = this.buckets[k];
            if (!bucket) {
                bucket = this.buckets[k] = [];
                this.filledBuckets.push(bucket);
            }
            if (bucket.indexOf(sprite) < 0)
                bucket.push(sprite);
        }

        insertAABB(sprite: Sprite) {
            const left = sprite.left;
            const top = sprite.top;
            const xn = Math.idiv(sprite.width + this.cellWidth - 1, this.cellWidth);
            const yn = Math.idiv(sprite.height + this.cellHeight - 1, this.cellHeight);
            for (let x = 0; x <= xn; x++)
                for (let y = 0; y <= yn; y++)
                    this.insertAtKey(left + Math.min(sprite.width, x * this.cellWidth), top + Math.min(sprite.height, y * this.cellHeight), sprite)
        }

        private mergeAtKey(x: number, y: number, layer: number, n: Sprite[]) {
            const k = this.key(x, y);
            const bucket = this.buckets[k];
            if (bucket) {
                for (const sprite of bucket)
                    if ((sprite.layer & layer)
                        && n.indexOf(sprite) < 0)
                        n.push(sprite);
            }
        }

        toString() {
            return `${this.buckets.length} buckets, ${this.buckets.filter(b => !!b).length} filled`;
        }
    }
}

game.sprites.ts:
/*
Frame handlers:
 10 - physics and collisions
 20 - frame()
 60 - screen/sprite background
 90 - drawing sprites
 95 - drawing score
100 - loops.menu()
200 - screen refresh
*/

/**
 * Sprites on screen
 */
//% weight=99 color="#4B7BEC" icon="\uf1d8"
//% groups='["Create", "Physics", "Effects", "Projectiles", "Overlaps", "Lifecycle"]'
namespace sprites {
    export class FollowingSprite {
        constructor(
            public self: Sprite,
            public target: Sprite,
            public rate: number,
            public turnRate: number
        ) { }
    }

    /**
     * Create a new sprite from an image
     * @param img the image
     */
    //% group="Create"
    //% blockId=spritescreate block="sprite %img=screen_image_picker of kind %kind=spritekind"
    //% expandableArgumentMode=toggle
    //% blockSetVariable=mySprite
    //% weight=100 help=sprites/create
    export function create(img: Image, kind?: number): Sprite {
        const scene = game.currentScene();
        const sprite = new Sprite(img)
        sprite.setKind(kind);
        scene.physicsEngine.addSprite(sprite);

        // run on created handlers
        scene.createdHandlers
            .filter(h => h.kind == kind)
            .forEach(h => h.handler(sprite));

        return sprite
    }

    /**
     * Create a new sprite from an image
     * @param img the image
     */
    //% group="Create"
    //% blockId=spritescreatenoset block="sprite %img=screen_image_picker of kind %kind=spritekind"
    //% blockAliasFor="sprites.create"
    //% expandableArgumentMode=toggle
    //% weight=99 help=sprites/create
    //% duplicateShadowOnDrag
    export function __create(img: Image, kind?: number): Sprite {
        return sprites.create(img, kind);
    }

    //% group="Effects"
    //% weight=80
    //% blockId=spritedestroy2 block="destroy $sprite || with $effect effect for $duration ms"
    //% sprite.shadow=variables_get
    //% sprite.defl=mySprite
    //% duration.shadow=timePicker
    //% expandableArgumentMode="toggle"
    //% help=sprites/sprite/destroy
    export function destroy(sprite: Sprite, effect?: effects.ParticleEffect, duration?: number) {
        if (!sprite) return;
        sprite.destroy(effect, duration);
    }

    /**
     * Return an array of all sprites of the given kind.
     * @param kind the target kind
     */
    //% blockId=allOfKind block="array of sprites of kind %kind=spritekind"
    //% weight=87 help=sprites/all-of-kind
    export function allOfKind(kind: number): Sprite[] {
        const spritesByKind = game.currentScene().spritesByKind;
        if (!(kind >= 0) || !spritesByKind[kind]) return [];
        else return spritesByKind[kind].sprites();
    }

    /**
     * Destroys all sprites of the given kind.
     */
    //% group="Effects"
    //% weight=79 help=sprites/destroy-all-sprites-of-kind
    //% blockId=sprites_destroy_all_sprites_of_kind
    //% block="destroy all sprites of kind $kind || with $effect effect for $duration ms"
    //% kind.shadow=spritekind
    //% duration.shadow=timePicker
    //% expandableArgumentMode="toggle"
    export function destroyAllSpritesOfKind(kind: number, effect?: effects.ParticleEffect, duration?: number) {
        for (const sprite of allOfKind(kind)) {
            sprite.destroy(effect, duration);
        }
    }

    /**
     * Create a new sprite with a given speed, and place it at the edge of the screen so it moves towards the middle.
     * The sprite auto-destroys when it leaves the screen. You can modify position after it's created.
     */
    //% group="Projectiles"
    //% blockId=spritescreateprojectilefromside block="projectile %img=screen_image_picker from side with vx %vx vy %vy"
    //% vx.shadow=spriteSpeedPicker
    //% vy.shadow=spriteSpeedPicker
    //% weight=99 help=sprites/create-projectile-from-side
    //% blockSetVariable=projectile
    //% inlineInputMode=inline
    export function createProjectileFromSide(img: Image, vx: number, vy: number) {
        return createProjectile(img, vx, vy, SpriteKind.Projectile);
    }

    /**
     * Create a new sprite with a given speed that starts from the location of another sprite.
     * The sprite auto-destroys when it leaves the screen. You can modify position after it's created.
     */
    //% group="Projectiles"
    //% blockId=spritescreateprojectilefromsprite block="projectile %img=screen_image_picker from %sprite=variables_get(mySprite) with vx %vx vy %vy"
    //% vx.shadow=spriteSpeedPicker
    //% vy.shadow=spriteSpeedPicker
    //% sprite.defl=mySprite
    //% weight=99 help=sprites/create-projectile-from-sprite
    //% blockSetVariable=projectile
    //% inlineInputMode=inline
    export function createProjectileFromSprite(img: Image, sprite: Sprite, vx: number, vy: number): Sprite {
        return createProjectile(img, vx, vy, SpriteKind.Projectile, sprite);
    }

    /**
     * Create a new sprite with given speed, and place it at the edge of the screen so it moves towards the middle.
     * The sprite auto-destroys when it leaves the screen. You can modify position after it's created.
     */
    //% group="Projectiles"
    //% blockId=spritescreateprojectile block="projectile %img=screen_image_picker vx %vx vy %vy of kind %kind=spritekind||from sprite %sprite=variables_get(mySprite)"
    //% weight=99 help=sprites/create-projectile
    //% blockSetVariable=projectile
    //% inlineInputMode=inline
    //% expandableArgumentMode=toggle
    //% deprecated=true blockHidden=true
    export function createProjectile(img: Image, vx: number, vy: number, kind?: number, sprite?: Sprite) {
        const s = sprites.create(img, kind || SpriteKind.Projectile);
        const sc = game.currentScene();

        s.vx = vx;
        s.vy = vy;

        if (sprite) {
            s.setPosition(sprite.x, sprite.y);
        } else {
            // put it at the edge of the screen so that it moves towards the middle
            // If the scene has a tile map, place the sprite fully on the screen
            const xOff = sc.tileMap ? -(s.width >> 1) : (s.width >> 1) - 1;
            const yOff = sc.tileMap ? -(s.height >> 1) : (s.height >> 1) - 1;
            const cam = game.currentScene().camera;

            let initialX = cam.offsetX;
            let initialY = cam.offsetY;

            if (vx < 0) {
                initialX += screen.width + xOff;
            } else if (vx > 0) {
                initialX += -xOff;
            }

            if (vy < 0) {
                initialY += screen.height + yOff;
            } else if (vy > 0) {
                initialY += -yOff;
            }

            s.setPosition(initialX, initialY);
        }

        s.flags |= sprites.Flag.AutoDestroy | sprites.Flag.DestroyOnWall;

        return s;
    }

    export enum Flag {
        None = 0, // no flags are set
        // 1 << 0 was previously used for Ghost / is now available.
        Destroyed = 1 << 1, // whether the sprite has been destroyed or not
        AutoDestroy = 1 << 2, // remove the sprite when no longer visible
        StayInScreen = 1 << 3, // sprite cannot move outside the camera region
        DestroyOnWall = 1 << 4, // destroy sprite on contact with wall
        BounceOnWall = 1 << 5, // Bounce on walls
        ShowPhysics = 1 << 6, // display position, velocity, acc
        Invisible = 1 << 7, // makes the sprite invisible, so it does not show up on the screen
        IsClipping = 1 << 8, // whether the sprite is currently clipping into a wall. This can happen when a sprite is created or moved explicitly.
        RelativeToCamera = 1 << 9, // draw relative to the camera, not the world (e.g. HUD elements)
        GhostThroughTiles = 1 << 10, // No overlaps with tiles
        GhostThroughWalls = 1 << 11, // No collisions with walls
        GhostThroughSprites = 1 << 12, // No overlaps with other sprites
        HitboxOverlaps = 1 << 13, // If set, overlaps with this sprite are based off of both sprites' hitboxes and not pixel perfect
        Ghost = sprites.Flag.GhostThroughSprites | sprites.Flag.GhostThroughWalls | sprites.Flag.GhostThroughTiles, // doesn't collide with other sprites or walls
    }
}

game.spritesay.ts:
namespace sprites {
    export class BaseSpriteSayRenderer {
        constructor(public text: string, public fgColor: number, public bgColor: number) {
        }

        draw(screen: Image, camera: scene.Camera, owner: Sprite) {

        }

        update(dt: number, camera: scene.Camera, owner: Sprite) {

        }

        destroy() {

        }
    }

    export class SpriteSayRenderer extends BaseSpriteSayRenderer {
        static drawSayFrame(textLeft: number, textTop: number, textWidth: number, textHeight: number, speakerX: number, speakerY: number, color: number, canvas: Image) {
            if (textLeft + textWidth < 0 || textTop + textHeight < 0 || textLeft > canvas.width || textTop > canvas.height) return;

            if (textHeight) {
                // Draw main rectangle
                canvas.fillRect(
                    textLeft,
                    textTop,
                    textWidth,
                    textHeight,
                    color
                );

                // Draw lines around the rectangle to give it a bubble shape
                canvas.fillRect(
                    textLeft - 1,
                    textTop + 1,
                    1,
                    textHeight - 2,
                    color
                );
                canvas.fillRect(
                    textLeft + textWidth,
                    textTop + 1,
                    1,
                    textHeight - 2,
                    color
                );
                canvas.fillRect(
                    textLeft + 1,
                    textTop - 1,
                    textWidth - 2,
                    1,
                    color
                );
                canvas.fillRect(
                    textLeft + 1,
                    textTop + textHeight,
                    textWidth - 2,
                    1,
                    color
                );

                // If the speaker location is within the bubble, don't draw an arrow
                if (speakerX > textLeft && speakerX < textLeft + textWidth && speakerY > textTop && speakerY < textTop + textHeight) return;

                const xDiff = Math.max(
                    Math.abs(speakerX - textLeft),
                    Math.abs(speakerX - (textLeft + textWidth))
                );

                const yDiff = Math.max(
                    Math.abs(speakerY - textHeight),
                    Math.abs(speakerY - (textHeight + textHeight))
                );

                // Draw the arrow
                if (xDiff > yDiff) {
                    if (speakerX > textLeft + textWidth) {
                        const anchorY = Math.max(Math.min(speakerY, textTop + textHeight - 4), textTop + 5);
                        canvas.fillRect(
                            textLeft + textWidth + 1,
                            anchorY - 2,
                            1,
                            3,
                            color
                        );
                        canvas.fillRect(
                            textLeft + textWidth + 2,
                            anchorY - 1,
                            1,
                            1,
                            color
                        );
                    }
                    else if (speakerX < textLeft) {
                        const anchorY = Math.max(Math.min(speakerY, textTop + textHeight - 4), textTop + 5);
                        canvas.fillRect(
                            textLeft - 2,
                            anchorY - 2,
                            1,
                            3,
                            color
                        );
                        canvas.fillRect(
                            textLeft - 3,
                            anchorY - 1,
                            1,
                            1,
                            color
                        );
                    }
                    else if (speakerY > textTop + textHeight) {
                        const anchorX = Math.max(Math.min(speakerX, textLeft + textWidth - 4), textLeft + 5);
                        canvas.fillRect(
                            anchorX - 2,
                            textTop + textHeight + 1,
                            3,
                            1,
                            color
                        );
                        canvas.fillRect(
                            anchorX - 1,
                            textTop + textHeight + 2,
                            1,
                            1,
                            color
                        );
                    }
                    else if (speakerY < textTop) {
                        const anchorX = Math.max(Math.min(speakerX, textLeft + textWidth - 4), textLeft + 5);
                        canvas.fillRect(
                            anchorX - 2,
                            textTop - 2,
                            3,
                            1,
                            color
                        );
                        canvas.fillRect(
                            anchorX - 1,
                            textTop - 3,
                            1,
                            1,
                            color
                        );
                    }
                }
                else {
                    if (speakerY > textTop + textHeight) {
                        const anchorX = Math.max(Math.min(speakerX, textLeft + textWidth - 4), textLeft + 5);
                        canvas.fillRect(
                            anchorX - 2,
                            textTop + textHeight + 1,
                            3,
                            1,
                            color
                        );
                        canvas.fillRect(
                            anchorX - 1,
                            textTop + textHeight + 2,
                            1,
                            1,
                            color
                        );
                    }
                    else if (speakerY < textTop) {
                        const anchorX = Math.max(Math.min(speakerX, textLeft + textWidth - 4), textLeft + 5);
                        canvas.fillRect(
                            anchorX - 2,
                            textTop - 2,
                            3,
                            1,
                            color
                        );
                        canvas.fillRect(
                            anchorX - 1,
                            textTop - 3,
                            1,
                            1,
                            color
                        );
                    }
                    else if (speakerX > textLeft + textWidth) {
                        const anchorY = Math.max(Math.min(speakerY, textTop + textHeight - 4), textTop + 5);
                        canvas.fillRect(
                            textLeft + textWidth + 1,
                            anchorY - 2,
                            1,
                            3,
                            color
                        );
                        canvas.fillRect(
                            textLeft + textWidth + 2,
                            anchorY - 1,
                            1,
                            1,
                            color
                        );
                    }
                    else if (speakerX < textLeft) {
                        const anchorY = Math.max(Math.min(speakerY, textTop + textHeight - 4), textTop + 5);
                        canvas.fillRect(
                            textLeft - 2,
                            anchorY - 2,
                            1,
                            3,
                            color
                        );
                        canvas.fillRect(
                            textLeft - 3,
                            anchorY - 1,
                            1,
                            1,
                            color
                        );
                    }
                }
            }
        }

        protected renderText: RenderText;
        protected animation: RenderTextAnimation;

        constructor(text: string, fg: number, bg: number, animated: boolean, timeOnScreen: number) {
            super(text, fg, bg);

            this.renderText = new sprites.RenderText(text, 100);
            if (animated) {
                this.animation = new sprites.RenderTextAnimation(this.renderText, 40);
                if (timeOnScreen >= 0) {
                    const numberOfPauses = this.animation.numPages() + 1;
                    const pauseTime = Math.min((timeOnScreen / (2 * numberOfPauses)) | 0, 1000);
                    this.animation.setPauseLength(pauseTime);
                    this.animation.setTextSpeed(this.renderText.printableCharacters() * 1000 / (timeOnScreen - pauseTime * numberOfPauses))
                }

                this.animation.start();
            }
        }

        draw(screen: Image, camera: scene.Camera, owner: Sprite) {
            const ox = (owner.flags & sprites.Flag.RelativeToCamera) ? 0 : camera.drawOffsetX;
            const oy = (owner.flags & sprites.Flag.RelativeToCamera) ? 0 : camera.drawOffsetY;

            const l = Math.floor(owner.left - ox);
            const t = Math.floor(owner.top - oy);

            const height = this.animation ? this.animation.currentHeight() : this.renderText.height;
            const width = this.animation ? this.animation.currentWidth() : this.renderText.width;
            const sayLeft = l + (owner.width >> 1) - (width >> 1);
            const sayTop =  t - height - 4;

            if (sayLeft + width < 0 || sayTop + height < 0 || sayLeft > screen.width || sayTop > screen.height) return;

            SpriteSayRenderer.drawSayFrame(sayLeft, sayTop, width, height, owner.x - ox, owner.y - oy, this.bgColor, screen);

            if (height) {
                if (this.animation) {
                    this.animation.draw(screen, sayLeft, sayTop, this.fgColor);
                }
                else {
                    this.renderText.draw(screen, sayLeft, sayTop, this.fgColor);
                }
            }
        }
    }

    export class LegacySpriteSayRenderer extends BaseSpriteSayRenderer {
        protected sayBubbleSprite: Sprite;
        protected updateSay: (dt: number, camera: scene.Camera) => void;

        constructor(text: string, timeOnScreen: number, owner: Sprite, fg: number, bg: number) {
            super(text, fg, bg);

            const textToDisplay = console.inspect(text).split("\n").join(" ");

            let pixelsOffset = 0;
            let holdTextSeconds = 1.5;
            let bubblePadding = 4;
            let maxTextWidth = 100;
            let font = image.getFontForText(textToDisplay);
            let startX = 2;
            let startY = 2;
            let bubbleWidth = textToDisplay.length * font.charWidth + bubblePadding;
            let maxOffset = textToDisplay.length * font.charWidth - maxTextWidth;
            let bubbleOffset: number = Fx.toInt(owner._hitbox.oy);
            let needsRedraw = true;

            // sets the default scroll speed in pixels per second
            let speed = 45;

            // Calculates the speed of the scroll if scrolling is needed and a time is specified
            if (timeOnScreen && maxOffset > 0) {
                speed = (maxOffset + (2 * maxTextWidth)) / (timeOnScreen / 1000);
                speed = Math.max(speed, 45);
                holdTextSeconds = maxTextWidth / speed;
                holdTextSeconds = Math.min(holdTextSeconds, 1.5);
            }

            if (timeOnScreen) {
                timeOnScreen = timeOnScreen + game.runtime();
            }

            if (bubbleWidth > maxTextWidth + bubblePadding) {
                bubbleWidth = maxTextWidth + bubblePadding;
            } else {
                maxOffset = -1;
            }

            // reuse previous sprite if possible
            const imgh = font.charHeight + bubblePadding;
            if (!this.sayBubbleSprite
                || this.sayBubbleSprite.width != bubbleWidth
                || this.sayBubbleSprite.height != imgh) {
                const sayImg = image.create(bubbleWidth, imgh);
                if (this.sayBubbleSprite) // sprite with same image size, we can reuse it
                    this.sayBubbleSprite.setImage(sayImg);
                else { // needs a new sprite
                    this.sayBubbleSprite = sprites.create(sayImg, -1);
                    this.sayBubbleSprite.setFlag(SpriteFlag.Ghost, true);
                    this.sayBubbleSprite.setFlag(SpriteFlag.RelativeToCamera, !!(owner.flags & sprites.Flag.RelativeToCamera))
                }
            }
            this.updateSay = (dt, camera) => {
                // The minus 2 is how much transparent padding there is under the sayBubbleSprite
                this.sayBubbleSprite.y = owner.top + bubbleOffset - ((font.charHeight + bubblePadding) >> 1) - 2;
                this.sayBubbleSprite.x = owner.x;
                this.sayBubbleSprite.z = owner.z + 1;

                // Update box stuff as long as timeOnScreen doesn't exist or it can still be on the screen
                if (!timeOnScreen || timeOnScreen > game.runtime()) {
                    // move bubble
                    if (!owner.isOutOfScreen(camera)) {
                        const ox = camera.offsetX;
                        const oy = camera.offsetY;

                        if (this.sayBubbleSprite.left - ox < 0) {
                            this.sayBubbleSprite.left = 0;
                        }

                        if (this.sayBubbleSprite.right - ox > screen.width) {
                            this.sayBubbleSprite.right = screen.width;
                        }

                        // If sprite bubble above the sprite gets cut off on top, place the bubble below the sprite
                        if (this.sayBubbleSprite.top - oy < 0) {
                            this.sayBubbleSprite.y = (this.sayBubbleSprite.y - 2 * owner.y) * -1;
                        }
                    }

                    // Pauses at beginning of text for holdTextSeconds length
                    if (holdTextSeconds > 0) {
                        holdTextSeconds -= game.eventContext().deltaTime;
                        // If scrolling has reached the end, start back at the beginning
                        if (holdTextSeconds <= 0 && pixelsOffset > 0) {
                            pixelsOffset = 0;
                            holdTextSeconds = maxTextWidth / speed;
                            needsRedraw = true;
                        }
                    } else {
                        pixelsOffset += dt * speed;
                        needsRedraw = true;

                        // Pause at end of text for holdTextSeconds length
                        if (pixelsOffset >= maxOffset) {
                            pixelsOffset = maxOffset;
                            holdTextSeconds = maxTextWidth / speed;
                        }
                    }

                    if (needsRedraw) {
                        needsRedraw = false;
                        this.sayBubbleSprite.image.fill(this.bgColor);
                        // If maxOffset is negative it won't scroll
                        if (maxOffset < 0) {
                            this.sayBubbleSprite.image.print(textToDisplay, startX, startY, this.fgColor, font);

                        } else {
                            this.sayBubbleSprite.image.print(textToDisplay, startX - pixelsOffset, startY, this.fgColor, font);
                        }

                        // Left side padding
                        this.sayBubbleSprite.image.fillRect(0, 0, bubblePadding >> 1, font.charHeight + bubblePadding, this.bgColor);
                        // Right side padding
                        this.sayBubbleSprite.image.fillRect(bubbleWidth - (bubblePadding >> 1), 0, bubblePadding >> 1, font.charHeight + bubblePadding, this.bgColor);
                        // Corners removed
                        this.sayBubbleSprite.image.setPixel(0, 0, 0);
                        this.sayBubbleSprite.image.setPixel(bubbleWidth - 1, 0, 0);
                        this.sayBubbleSprite.image.setPixel(0, font.charHeight + bubblePadding - 1, 0);
                        this.sayBubbleSprite.image.setPixel(bubbleWidth - 1, font.charHeight + bubblePadding - 1, 0);
                    }
                } else {
                    // If can't update because of timeOnScreen then destroy the sayBubbleSprite and reset updateSay
                    this.updateSay = undefined;
                    this.sayBubbleSprite.destroy();
                    this.sayBubbleSprite = undefined;
                }
            }
            this.updateSay(0, game.currentScene().camera);
        }

        update(dt: number, camera: scene.Camera, owner: Sprite) {
            if (!this.sayBubbleSprite) return;
            this.updateSay(dt, camera);
            if (!this.sayBubbleSprite) return;

            this.sayBubbleSprite.setFlag(SpriteFlag.RelativeToCamera, !!(owner.flags & SpriteFlag.RelativeToCamera));

            if (owner.flags & Flag.Destroyed) this.destroy();
        }

        destroy() {
            if (this.sayBubbleSprite) this.sayBubbleSprite.destroy();
            this.sayBubbleSprite = undefined;
        }
    }
}

game.spriteset.ts:
namespace sprites {
    /*
    * A set of sprites
    */
    export class SpriteSet {
        private _sprites: Sprite[];

        /**
         * Create a new set from an array of sprites
         * @param sprites
         */
        static createFromArray(sprites: Sprite[]): SpriteSet {
            const sp = new SpriteSet();
            const n = sprites.length;
            for (let i = 0; i < n; ++i)
                sp.add(sprites[i]);
            return sp;
        }

        constructor() {
            this._sprites = [];
        }

        /**
         * Gets the number of sprites in the set
         */
        get length() {
            return this._sprites.length;
        }

        /**
         * Gets the snapshot of the current list of sprites
         */
        sprites() {
            return this._sprites.filter(s => !(s.flags & sprites.Flag.Destroyed));
        }

        /**
         * Adds the sprite, returns true if added; false if the sprite was already in the set
         * @param sprite
         */
        add(sprite: Sprite): boolean {
            if (!sprite) return false; // don't add nulls

            // scan if in set
            if (this.contains(sprite))
                return false;
            this._sprites.push(sprite);
            return true;
        }

        /**
         * Adds sprite and removes from old set. Returns true if sprite was in old set and not in new set.
         * @param oldSet
         * @param sprite
         */
        addFrom(oldSet: SpriteSet, sprite: Sprite): boolean {
            const removed = oldSet.remove(sprite);
            const added = this.add(sprite);
            return removed && added;
        }

        /**
         * Removes sprite from set. Returns true if the sprite was in the set
         * @param sprite
         */
        remove(sprite: Sprite): boolean {
            const i = this._sprites.indexOf(sprite);
            if (i > -1) {
                this._sprites.splice(i, 1);
                return true;
            }
            return false;
        }

        /**
         * Checks if the sprite is part of the set
         * @param sprite
         */
        contains(sprite: Sprite): boolean {
            return this._sprites.indexOf(sprite) > -1;
        }

        /**
         * Removes all the sprites from the set
        */
        clear() {
            this._sprites.splice(0, this._sprites.length);
        }

        /**
         * Removes the last sprite in the set
         */
        pop(): Sprite {
            return this._sprites.pop();
        }

        toString() {
            return `${this.length} sprites`
        }
    }
}

