#include "pxt.h"

// This adds about 1.2k of binary size, but allows for displaying
// panic codes regardless of heap state etc. with IRQs disabled.

#define ST7735_NOP 0x00
#define ST7735_SWRESET 0x01
#define ST7735_RDDID 0x04
#define ST7735_RDDST 0x09

#define ST7735_SLPIN 0x10
#define ST7735_SLPOUT 0x11
#define ST7735_PTLON 0x12
#define ST7735_NORON 0x13

#define ST7735_INVOFF 0x20
#define ST7735_INVON 0x21
#define ST7735_DISPOFF 0x28
#define ST7735_DISPON 0x29
#define ST7735_CASET 0x2A
#define ST7735_RASET 0x2B
#define ST7735_RAMWR 0x2C
#define ST7735_RAMRD 0x2E

#define ST7735_PTLAR 0x30
#define ST7735_COLMOD 0x3A
#define ST7735_MADCTL 0x36

#define ST7735_FRMCTR1 0xB1
#define ST7735_FRMCTR2 0xB2
#define ST7735_FRMCTR3 0xB3
#define ST7735_INVCTR 0xB4

#define ST7735_GMCTRP1 0xE0
#define ST7735_GMCTRN1 0xE1

#define MADCTL_MY 0x80
#define MADCTL_MX 0x40
#define MADCTL_MV 0x20
#define MADCTL_ML 0x10
#define MADCTL_RGB 0x00
#define MADCTL_BGR 0x08
#define MADCTL_MH 0x04

namespace _pxt_panic {

// target_panic has been called
static bool panicMode = false;

#define DELAY 0x80

class ST7735 {
    Pin *sck, *mosi, *dc, *cs;

    void sendBytes(uint8_t *ptr, uint32_t len);
    void sendCmd(uint8_t *buf, int len);
    void sendCmdSeq(const uint8_t *buf);
    void configure(uint8_t madctl, uint32_t frmctr1);

  public:
    uint16_t width, height;
    void fill(int color, int numpixels);
    void setAddrWindow(int x, int y, int w, int h);
    void init();
    void drawNumber(int idx, int x, int y, int color);
};

// clang-format off
static const uint8_t initCmds[] = {
    ST7735_SWRESET,   DELAY,  //  1: Software reset, 0 args, w/delay
      120,                    //     150 ms delay
    ST7735_SLPOUT ,   DELAY,  //  2: Out of sleep mode, 0 args, w/delay
      120,                    //     500 ms delay
    ST7735_INVOFF , 0      ,  // 13: Don't invert display, no args, no delay
    ST7735_COLMOD , 1      ,  // 15: set color mode, 1 arg, no delay:
      0x03,                  //     12-bit color

    ST7735_NORON  ,    DELAY, //  3: Normal display on, no args, w/delay
      10,                     //     10 ms delay
    ST7735_DISPON ,    DELAY, //  4: Main screen turn on, no args w/delay
      10,
    0, 0 // END
};
// clang-format on

static const uint8_t numbers[] = {
    0x06, 0x09, 0x09, 0x09, 0x06, // 0
    0x04, 0x06, 0x04, 0x04, 0x0e, // 1
    0x07, 0x08, 0x06, 0x01, 0x0f, // 2
    0x0f, 0x08, 0x04, 0x09, 0x06, // 3
    0x0c, 0x0a, 0x09, 0x1f, 0x08, // 4
    0x1f, 0x01, 0x0f, 0x10, 0x0f, // 5
    0x08, 0x04, 0x0e, 0x11, 0x0e, // 6
    0x1f, 0x08, 0x04, 0x02, 0x01, // 7
    0x0e, 0x11, 0x0e, 0x11, 0x0e, // 8
    0x0e, 0x11, 0x0e, 0x04, 0x02, // 9
    0x11, 0x00, 0x0e, 0x1b, 0x11, // :(
    // 0x11, 0x04, 0x04, 0x0a, 0x11, // :(
};

void ST7735::sendBytes(uint8_t *ptr, uint32_t len) {
    uint8_t mask = 0, b;
    for (;;) {
        if (!mask) {
            if (!len--)
                break;
            mask = 0x80;
            b = *ptr++;
        }
        mosi->setDigitalValue((b & mask) ? 1 : 0);
        sck->setDigitalValue(1);
        mask >>= 1;
        sck->setDigitalValue(0);
    }
}

void ST7735::fill(int color, int numpixels) {
    uint8_t cmd[20] = {ST7735_RAMWR};
    sendCmd(cmd, 1);

    dc->setDigitalValue(1);
    if (cs)
        cs->setDigitalValue(0);

    cmd[0] = color >> 4;
    cmd[1] = (color << 4) | (color >> 8);
    cmd[2] = color;

    int n = (numpixels + 1) >> 1;
    while (n--) {
        sendBytes(cmd, 3);
    }

    if (cs)
        cs->setDigitalValue(1);
}

void ST7735::sendCmd(uint8_t *buf, int len) {
    // make sure cmd isn't on stack
    dc->setDigitalValue(0);
    if (cs)
        cs->setDigitalValue(0);
    sendBytes(buf, 1);
    dc->setDigitalValue(1);
    len--;
    buf++;
    if (len > 0)
        sendBytes(buf, len);
    if (cs)
        cs->setDigitalValue(1);
}

static void busy_wait_us(int ms) {
    target_wait_us(ms);
    /*
    // this is for 120MHz
    while (ms--) {
        for (int i = 0; i < 30; ++i)
            asm volatile("nop");
    }
    */
}

void ST7735::sendCmdSeq(const uint8_t *buf) {
    uint8_t cmdBuf[32];

    while (*buf) {
        cmdBuf[0] = *buf++;
        int v = *buf++;
        int len = v & ~DELAY;
        // note that we have to copy to RAM
        memcpy(cmdBuf + 1, buf, len);
        sendCmd(cmdBuf, len + 1);
        buf += len;
        if (v & DELAY) {
            busy_wait_us(1000 * *buf++);
        }
    }
}

void ST7735::setAddrWindow(int x, int y, int w, int h) {
    w += x - 1;
    h += y - 1;
    uint8_t cmd0[] = {ST7735_RASET, 0, (uint8_t)x, (uint8_t)(w >> 8), (uint8_t)w};
    uint8_t cmd1[] = {ST7735_CASET, 0, (uint8_t)y, (uint8_t)(h >> 8), (uint8_t)h};
    sendCmd(cmd1, sizeof(cmd1));
    sendCmd(cmd0, sizeof(cmd0));
}

void ST7735::init() {
    mosi = LOOKUP_PIN(DISPLAY_MOSI);
    sck = LOOKUP_PIN(DISPLAY_SCK);
    cs = LOOKUP_PIN(DISPLAY_CS);
    dc = LOOKUP_PIN(DISPLAY_DC);
    auto bl = LOOKUP_PIN(DISPLAY_BL);
    auto rst = LOOKUP_PIN(DISPLAY_RST);

    if (cs)
        cs->setDigitalValue(1);
    dc->setDigitalValue(1);

    if (bl) {
        bl->setDigitalValue(1);
    }

    if (rst) {
        rst->setDigitalValue(0);
        busy_wait_us(20 * 1000);
        rst->setDigitalValue(1);
        busy_wait_us(20 * 1000);
    }

    uint32_t cfg0 = getConfig(CFG_DISPLAY_CFG0, 0x40);
    uint32_t frmctr1 = getConfig(CFG_DISPLAY_CFG1, 0x000603);
    auto madctl = cfg0 & 0xff;

    sendCmdSeq(initCmds);
    configure(madctl, frmctr1);

    width = getConfig(CFG_DISPLAY_WIDTH, 160);
    height = getConfig(CFG_DISPLAY_HEIGHT, 128);
}

void ST7735::configure(uint8_t madctl, uint32_t frmctr1) {
    uint8_t cmd0[] = {ST7735_MADCTL, madctl};
    uint8_t cmd1[] = {ST7735_FRMCTR1, (uint8_t)(frmctr1 >> 16), (uint8_t)(frmctr1 >> 8),
                      (uint8_t)frmctr1};
    sendCmd(cmd0, sizeof(cmd0));
    sendCmd(cmd1, cmd1[3] == 0xff ? 3 : 4);
}

#define SIZE 4

void ST7735::drawNumber(int idx, int x, int y, int color) {
    const uint8_t *src = &numbers[idx * 5];
    for (int i = 0; i < 5; i++) {
        uint8_t ch = *src++;
        for (int j = 0; j < 5; j++) {
            if (ch & (1 << j)) {
                setAddrWindow(x + j * SIZE, y + i * SIZE, SIZE - 1, SIZE - 1);
                fill(color, (SIZE - 1) * (SIZE - 1));
            }
        }
    }
}

static void drawPanic(int code) {
    if (!LOOKUP_PIN(DISPLAY_MOSI))
        return;

    ST7735 display;

    display.init();
    display.setAddrWindow(0, 0, display.width, display.height);
    display.fill(0, display.width * display.height);

    display.drawNumber(10, 70, 20, 0xf00);
    int x = 50;
    int y = 60;
    display.drawNumber((code / 100) % 10, x, y, 0xff0);
    x += 24;
    display.drawNumber((code / 10) % 10, x, y, 0xff0);
    x += 24;
    display.drawNumber((code / 1) % 10, x, y, 0xff0);
    x += 24;
}

extern "C" __attribute__((weak)) void platform_panic(int statusCode) {}

extern "C" void target_panic(int statusCode) {
    __disable_irq();      // low-level disable
    target_disable_irq(); // make sure they stay disabled in DMESG()

    DMESG("*** CODAL PANIC : [%d]", statusCode);

    __disable_irq(); // disable IRQ after DMESG() again just to make sure

    if (panicMode) {
        // avoid recursive panic invocation
        while (1) {
        }
    }

    gcFreeze();

    // remember first panic code
    panicMode = true;

    drawPanic(statusCode);

    platform_panic(statusCode);

    auto led = LOOKUP_PIN(LED);

    const int unit = 100000;
    const int dit = unit;
    const int dat = 3 * unit;
    const int intra = unit;
    const int inter = 2 * unit;
    const int word = 6 * unit;
    while (1) {
        if (led) {
            // SOS
            // . . .
            for (int i = 0; i < 3; ++i) {
                led->setDigitalValue(1);
                busy_wait_us(dit);
                led->setDigitalValue(0);
                busy_wait_us(intra);
            }
            // inter character space
            busy_wait_us(inter);
            // - - -
            for (int i = 0; i < 3; ++i) {
                led->setDigitalValue(1);
                busy_wait_us(dat);
                led->setDigitalValue(0);
                busy_wait_us(intra);
            }
            // inter character space
            busy_wait_us(inter);
            // . . .
            for (int i = 0; i < 3; ++i) {
                led->setDigitalValue(1);
                busy_wait_us(dit);
                led->setDigitalValue(0);
                busy_wait_us(intra);
            }
            // inter character space
            busy_wait_us(word);
        }
    }
}

} // namespace _pxt_panic

screen---st7735.screen.cpp:
#include "pxt.h"
#include "ST7735.h"
#include "ILI9341.h"

#include "SPIScreenIO.h"
#ifdef STM32F4
#include "FSMCIO.h"
#endif

#include "jddisplay.h"

namespace pxt {

class WDisplay {
  public:
    ScreenIO *io;
    ST7735 *lcd;
    JDDisplay *smart;

    uint32_t currPalette[16];
    bool newPalette;
    bool inUpdate;

    uint8_t *screenBuf;
    Image_ lastStatus;

    uint16_t width, height;
    uint16_t displayHeight;
    uint8_t offX, offY;
    bool doubleSize;
    uint32_t palXOR;

    WDisplay() {
        uint32_t cfg2 = getConfig(CFG_DISPLAY_CFG2, 0x0);
        int conn = cfg2 >> 24;

        uint32_t cfg0 = getConfig(CFG_DISPLAY_CFG0, 0x40);
        uint32_t frmctr1 = getConfig(CFG_DISPLAY_CFG1, 0x000603);

        int dispTp = getConfig(CFG_DISPLAY_TYPE, DISPLAY_TYPE_ST7735);

        doubleSize = false;
        smart = NULL;

        auto miso = LOOKUP_PIN(DISPLAY_MISO);

        if (dispTp == DISPLAY_TYPE_SMART) {
            dispTp = smartConfigure(&cfg0, &frmctr1, &cfg2);
        }

        if (dispTp != DISPLAY_TYPE_SMART)
            miso = NULL; // only JDDisplay needs MISO, otherwise leave free

        SPI *spi = NULL;
        if (conn == 0) {
            spi = new CODAL_SPI(*LOOKUP_PIN(DISPLAY_MOSI), *miso, *LOOKUP_PIN(DISPLAY_SCK));
            io = new SPIScreenIO(*spi);
        } else if (conn == 1) {
#ifdef CODAL_CREATE_PARALLEL_SCREEN_IO
            io = CODAL_CREATE_PARALLEL_SCREEN_IO(cfg2 & 0xffffff, PIN(DISPLAY_MOSI),
                                                 PIN(DISPLAY_MISO));
#else
            target_panic(PANIC_SCREEN_ERROR);
#endif
        } else {
            target_panic(PANIC_SCREEN_ERROR);
        }

        if (dispTp == DISPLAY_TYPE_ST7735)
            lcd = new ST7735(*io, *LOOKUP_PIN(DISPLAY_CS), *LOOKUP_PIN(DISPLAY_DC));
        else if (dispTp == DISPLAY_TYPE_ILI9341) {
            lcd = new ILI9341(*io, *LOOKUP_PIN(DISPLAY_CS), *LOOKUP_PIN(DISPLAY_DC));
            doubleSize = true;
        } else if (dispTp == DISPLAY_TYPE_SMART) {
            lcd = NULL;
            smart = new JDDisplay(spi, LOOKUP_PIN(DISPLAY_CS), LOOKUP_PIN(DISPLAY_DC));
        } else
            target_panic(PANIC_SCREEN_ERROR);

        palXOR = (cfg0 & 0x1000000) ? 0xffffff : 0x000000;
        auto madctl = cfg0 & 0xff;
        offX = (cfg0 >> 8) & 0xff;
        offY = (cfg0 >> 16) & 0xff;

        DMESG("configure screen: FRMCTR1=%p MADCTL=%p type=%d", frmctr1, madctl, dispTp);

        if (spi) {
            auto freq = (cfg2 & 0xff);
            if (!freq)
                freq = 15;
            spi->setFrequency(freq * 1000000);
            spi->setMode(0);
            auto cs = LOOKUP_PIN(DISPLAY_CS);
            if (cs)
                cs->setDigitalValue(1);

            // make sure the SPI peripheral is initialized before toggling reset
            spi->write(0);
        }

        auto rst = LOOKUP_PIN(DISPLAY_RST);
        if (rst) {
            rst->setDigitalValue(0);
            fiber_sleep(20);
            rst->setDigitalValue(1);
            fiber_sleep(20);
        }

        if (lcd) {
            auto bl = LOOKUP_PIN(DISPLAY_BL);
            if (bl) {
                bl->setDigitalValue(1);
            }

            lcd->init();
            lcd->configure(madctl, frmctr1);
        }

        width = getConfig(CFG_DISPLAY_WIDTH, 160);
        height = getConfig(CFG_DISPLAY_HEIGHT, 128);
        displayHeight = height;
        setAddrMain();
        DMESG("screen: %d x %d, off=%d,%d", width, height, offX, offY);
        int sz = doubleSize ? (width >> 1) * (height >> 1) : width * height;
        screenBuf = (uint8_t *)app_alloc(sz / 2 + 20);

        lastStatus = NULL;
        registerGC((TValue *)&lastStatus);
        inUpdate = false;
    }

    uint32_t smartConfigure(uint32_t *cfg0, uint32_t *cfg1, uint32_t *cfg2) {
        uint32_t hc;

        DMESG("74HC: waiting...");

        // wait while nothing is connected
        for (;;) {
            auto rst = LOOKUP_PIN(DISPLAY_RST);
            if (rst) {
                rst->setDigitalValue(0);
                target_wait_us(10);
                rst->setDigitalValue(1);
                fiber_sleep(3); // in reality we need around 1.2ms
            }

            hc = readButtonMultiplexer(17);
            if (hc != 0)
                break;

            fiber_sleep(100);

            // the device will run without shield when the following is specified in user program:
            // namespace userconfig { export const DISPLAY_CFG0 = 0x02000080 }
            if (*cfg0 & 0x2000000) {
                DMESG("74HC: no wait requested");
                return DISPLAY_TYPE_ST7735;
            }
        }

        DMESG("74HC: %x", hc);

        // is the line forced up? if so, assume JDDisplay
        if (hc == 0x1FFFF) {
            disableButtonMultiplexer();
            return DISPLAY_TYPE_SMART;
        }

        hc = hc >> 1;

        // SER pin (or first bit of second HC) is orientation
        if (hc & 0x0010)
            *cfg0 = ((*cfg0) & 0xffffff3f) | 0x80;
        else
            *cfg0 = ((*cfg0) & 0xffffff3f) | 0x40;

        uint32_t configId = (hc & 0xe0) >> 5;

        switch (configId) {
        case 1:
            *cfg1 = 0x0603; // ST7735
            break;
        case 2:
            *cfg1 = 0xe14ff; // ILI9163C
            *cfg0 |= 0x08;   // BGR colors
            break;
        case 3:
            *cfg1 = 0x0603;     // ST7735
            *cfg0 |= 0x1000000; // inverted colors
            break;
        default:
            target_panic(PANIC_SCREEN_ERROR);
            break;
        }

        DMESG("config type: %d; cfg0=%x cfg1=%x", configId, *cfg0, *cfg1);

        *cfg2 = 32; // Damn the torpedoes! 32MHz

        return DISPLAY_TYPE_ST7735;
    }

    void setAddrStatus() {
        if (lcd)
            lcd->setAddrWindow(offX, offY + displayHeight, width, height - displayHeight);
        else
            smart->setAddrWindow(offX, offY + displayHeight, width, height - displayHeight);
    }
    void setAddrMain() {
        if (lcd)
            lcd->setAddrWindow(offX, offY, width, displayHeight);
        else
            smart->setAddrWindow(offX, offY, width, displayHeight);
    }
    void waitForSendDone() {
        if (lcd)
            lcd->waitForSendDone();
        else
            smart->waitForSendDone();
    }
    int sendIndexedImage(const uint8_t *src, unsigned width, unsigned height, uint32_t *palette) {
        if (lcd)
            return lcd->sendIndexedImage(src, width, height, palette);
        else
            return smart->sendIndexedImage(src, width, height, palette);
    }
};

SINGLETON_IF_PIN(WDisplay, DISPLAY_MOSI);

//%
int setScreenBrightnessSupported() {
    auto display = getWDisplay();
    if (display && display->smart)
        return 1;

    auto bl = LOOKUP_PIN(DISPLAY_BL);
    if (!bl)
        return 0;
#ifdef SAMD51
    if (bl->name == PA06)
        return 0;
#endif
#ifdef NRF52_SERIES
    // PWM not implemented yet
    return 0;
#else
    return 1;
#endif
}

//%
void setScreenBrightness(int level) {
    if (level < 0)
        level = 0;
    if (level > 100)
        level = 100;

    auto display = getWDisplay();
    if (display && display->smart) {
        display->smart->brightness = level;
        return;
    }

    auto bl = LOOKUP_PIN(DISPLAY_BL);
    if (!bl)
        return;

    if (level == 0)
        bl->setDigitalValue(0);
    else if (level == 100)
        bl->setDigitalValue(1);
    else {
        if (setScreenBrightnessSupported()) {
            bl->setAnalogPeriodUs(1000);
            bl->setAnalogValue(level * level * 1023 / 10000);
        }
    }
}

//%
void setPalette(Buffer buf) {
    auto display = getWDisplay();
    if (!display)
        return;

    if (48 != buf->length)
        target_panic(PANIC_SCREEN_ERROR);
    for (int i = 0; i < 16; ++i) {
        display->currPalette[i] =
            (buf->data[i * 3] << 16) | (buf->data[i * 3 + 1] << 8) | (buf->data[i * 3 + 2] << 0);
        display->currPalette[i] ^= display->palXOR;
    }
    display->newPalette = true;
}

//%
void setupScreenStatusBar(int barHeight) {
    auto display = getWDisplay();
    if (!display)
        return;
    if (!display->doubleSize) {
        display->displayHeight = display->height - barHeight;
        display->setAddrMain();
    }
}

//%
void updateScreenStatusBar(Image_ img) {
    auto display = getWDisplay();
    if (!display)
        return;

    if (!img)
        return;
    display->lastStatus = img;
}

//%
void updateScreen(Image_ img) {
    auto display = getWDisplay();
    if (!display)
        return;

    if (display->inUpdate)
        return;

    display->inUpdate = true;

    auto mult = display->doubleSize ? 2 : 1;

    if (img) {
        if (img->bpp() != 4 || img->width() * mult != display->width ||
            img->height() * mult != display->displayHeight)
            target_panic(PANIC_SCREEN_ERROR);

        // DMESG("wait for done");
        display->waitForSendDone();

        auto palette = display->currPalette;

        if (display->newPalette) {
            display->newPalette = false;
        } else {
            // smart mode always sends palette
            if (!display->smart)
                palette = NULL;
        }

        memcpy(display->screenBuf, img->pix(), img->pixLength());

        // DMESG("send");
        display->sendIndexedImage(display->screenBuf, img->width(), img->height(), palette);
    }

    if (display->lastStatus && !display->doubleSize) {
        display->waitForSendDone();
        img = display->lastStatus;
        auto barHeight = display->height - display->displayHeight;
        if (img->bpp() != 4 || barHeight != img->height() || img->width() != display->width)
            target_panic(PANIC_SCREEN_ERROR);
        memcpy(display->screenBuf, img->pix(), img->pixLength());
        display->setAddrStatus();
        display->sendIndexedImage(display->screenBuf, img->width(), img->height(), NULL);
        display->waitForSendDone();
        display->setAddrMain();
        display->lastStatus = NULL;
    }

    display->inUpdate = false;
}

//%
void updateStats(String msg) {
    // ignore...
}

} // namespace pxt

