/**
 * Character LCD support
 */
//% icon="\uf0ae" color="#219E42" blockGap=8
//% groups='["Display", "Configuration"]'
namespace lcd {
    let _screen: CharacterLCD;

    /**
     * Gets the current LCD screen
     */
    export function screen(): CharacterLCD {
        if (_screen !== undefined) return _screen;

        const rs = pins.pinByCfg(DAL.CFG_PIN_LCD_RESET);
        const en = pins.pinByCfg(DAL.CFG_PIN_LCD_ENABLE);
        const db4 = pins.pinByCfg(DAL.CFG_PIN_LCD_DATALINE4);
        const db5 = pins.pinByCfg(DAL.CFG_PIN_LCD_DATALINE5);
        const db6 = pins.pinByCfg(DAL.CFG_PIN_LCD_DATALINE6);
        const db7 = pins.pinByCfg(DAL.CFG_PIN_LCD_DATALINE7);
        const columns = control.getConfigValue(DAL.CFG_NUM_LCD_COLUMNS, 16);
        const lines = control.getConfigValue(DAL.CFG_NUM_LCD_ROWS, 2);

        if (!rs || !en || !db4 || !db5 || !db6 || !db7) {
            _screen = null; // not supported
        }
        else {
            _screen = new CharacterLCDMono(rs, en, db4, db5, db6, db7, columns, lines);
        }
        return _screen;
    }

    /**
     * Shows a string on the LCD screen
     * @param text the text to show
     * @param line the line number starting at 1...
     */
    //% blockId=lcdshowstring block="lcd show string %text at line %line"
    //% line.min=1 line.max=2 line.defl=1
    //% parts="lcd"
    //% group="Display"
    export function showString(text: string, line?: number) {
        const l = screen();
        if (!l) return;
        if (line === undefined) line = 1;

        line = (line - 1) | 0;
        if (line < 0 || line >= l.lines) return; // out of range

        // insert text in line
        const lines = l.message.split('\n');
        // assign all lines within range
        text.split('\n')
            .filter((tl, i) => line + i < l.lines)
            .forEach((tl, i) => {
                lines[line + i] = tl;
            })
        // fill the holes
        for(let i = 0; i < lines.length; ++i) {
            if (lines[i] === undefined) lines[i] = "";
        }
        // reassemble text
        const message = lines.join('\n');

        l.message = message;
    }

    /**
     * Shows a number on the LCD screen
     * @param value the number to show
     */
    //% blockId=lcdshownumber block="lcd show number %value at line %line"
    //% line.min=1 line.max=2 line.defl=1
    //% parts="lcd"
    //% group="Display"
    export function showNumber(value: number, line?: number) {
        value = Math.round(value * 1000) / 1000;
        showString(value + "", line);
    }

        /**
     * Shows a name, value pair on the screen
     * @param value the numeric value
     * @param line the line number to print the text at (starting at 1), eg: 1
     */
    //% blockId=lcdshowvalue block="lcd show value %name: %text at line %line"
    //% inlineInputMode="inline" blockGap=8
    //% line.min=1 line.max=2 line.defl=1
    //% parts="lcd"
    //% group="Display"
    export function showValue(name: string, value: number, line: number) {
        value = Math.round(value * 1000) / 1000;
        showString((name ? name + ": " : "") + value, line);
    }

    /**
     * Clears the screen
     */
    //% blockId=lcdclear block="lcd clear"
    //% parts=lcd
    //% group="Display"
    export function clear() {
        const l = screen();
        if (!l) return;
        l.clear();
    }

    /**
     * Enables or disables display
     * @param enabled true to turn the display on; false otherwise
     */
    //% blockId=lcdsetdisplay block="lcd set display %enabled"
    //% enabled.shadow=toggleOnOff
    //% parts="lcd"
    //% group="Configuration"
    export function setDisplay(enabled: boolean) {
        const l = screen();
        if (!l) return;

        l.display = !!enabled;
    }

    /**
     * Enables or disables blinking
     * @param enabled true to blink
     */
    //% blockId=lcdsetblink block="lcd set blink %enabled"
    //% enabled.shadow=toggleOnOff
    //% group="Configuration"
    export function setBlink(enabled: boolean) {
        const l = screen();
        if (!l) return;

        l.blink = !!enabled;
    }

    /**
     * Show or hide cursor
     * @param enabled true to display cursor, false otherwise
     */
    //% blockId=lcdsetcursor block="lcd set curcor %enabled"
    //% enabled.shadow=toggleOnOff
    //% group="Configuration"
    export function setCursor(enabled: boolean) {
        const l = screen();
        if (!l) return;

        l.cursor = !!enabled;
    }
}

color.colorbuffer.ts:
namespace color {
    export enum ColorBufferLayout {
        /**
         * 24bit RGB color
         */
        RGB,
        /**
         * 32bit RGB color with alpha
         */
        ARGB
    }

    /**
     * A buffer of colors
     */
    export class ColorBuffer {
        layout: ColorBufferLayout;
        buf: Buffer;

        constructor(length: number, layout?: ColorBufferLayout) {
            this.layout = layout || ColorBufferLayout.RGB;
            this.buf = control.createBuffer((length | 0) * this.stride);
        }

        static fromBuffer(buffer: Buffer, layout: ColorBufferLayout) {
            const b = new ColorBuffer(0, layout);
            b.buf = buffer.slice();
            return b;
        }

        get stride() {
            return this.layout == ColorBufferLayout.RGB ? 3 : 4;
        }

        get length() {
            return Math.idiv(this.buf.length, this.stride);
        }

        color(index: number): number {
            index = index | 0;
            if (index < 0 || index >= this.length)
                return -1;

            const s = this.stride;
            const start = index * s;
            let c = 0;
            for (let i = 0; i < s; ++i)
                c = (c << 8) | (this.buf[start + i] & 0xff);
            return c;
        }

        setColor(index: number, color: number) {
            index = index | 0;
            if (index < 0 || index >= this.length) return;

            const s = this.stride;
            const start = index * s;
            for (let i = s - 1; i >= 0; --i) {
                this.buf[start + i] = color & 0xff;
                color = color >> 8;
            }
        }

        slice(start?: number, length?: number): ColorBuffer {
            start = start | 0;
            if (start < 0)
                start = this.length - start;

            if (length == undefined)
                length = this.length;
            length = Math.min(length, this.length - start);

            const output = new ColorBuffer(length, this.layout);
            for (let i = 0; i < length; ++i) {
                output.setColor(i, this.color(start + i));
            }

            return output;
        }

        /**
         * Writes the content of the src color buffer starting at the start dstOffset in the current buffer
         * @param dstOffset
         * @param src
         */
        write(dstOffset: number, src: ColorBuffer): void {
            if (this.layout == src.layout) {
                const d = (dstOffset | 0) * this.stride;
                this.buf.write(d, src.buf);
            } else {
                // different color layout
                const n = Math.min(src.length, this.length - dstOffset);
                for (let i = 0; i < n; ++i)
                    this.setColor(dstOffset + i, src.color(i));
            }
        }
    }

    /**
     * Converts an array of colors into a color buffer
     */
    export function createBuffer(colors: number[], layout?: ColorBufferLayout): color.ColorBuffer {
        const p = new ColorBuffer(colors.length, layout);
        const n = colors.length;
        for (let i = 0; i < n; i++) {
            p.setColor(i, colors[i]);
        }
        return p;
    }
}

